    pub updated_at: String,
    pub created_by: String,
}

impl Project {
    pub fn new(name: String, description: String, owner_id: String, created_by: String) -> Self {
        let now = chrono::Utc::now().to_rfc3339();
        Self {
            project_id: Uuid::new_v4().to_string(),
            conference_id: None,
            name,
            description,
            status: ProjectStatus::Planning,
            priority: ProjectPriority::Medium,
            owner_id,
            team_members: Vec::new(),
            start_date: None,
            target_end_date: None,
            actual_end_date: None,
            budget: None,
            budget_spent: 0.0,
            progress_percentage: 0.0,
            tags: Vec::new(),
            created_at: now.clone(),
            updated_at: now,
            created_by,
        }
    }

    /// Calculate progress based on task completion
    pub fn calculate_progress(&self, tasks: &[Task]) -> f32 {
        if tasks.is_empty() {
            return 0.0;
        }
        let completed = tasks
            .iter()
            .filter(|t| t.status == TaskStatus::Completed)
            .count();
        (completed as f32 / tasks.len() as f32) * 100.0
    }

    /// Check if project is overdue
    pub fn is_overdue(&self) -> bool {
        if let Some(target_date) = &self.target_end_date {
            if self.status != ProjectStatus::Completed {
                let now = chrono::Utc::now().to_rfc3339();
                return now > *target_date;
            }
        }
        false
    }

    /// Check if project is over budget
    pub fn is_over_budget(&self) -> bool {
        if let Some(budget) = self.budget {
            return self.budget_spent > budget;
        }
        false
    }
}

