  /**
   * Update session context
   * Invokes: core_update_session_context
   */
  async updateSessionContext(context: Record<string, any>): Promise<BridgeResponse<CoreSession>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: 'No active session',
        code: 'NO_SESSION',
      };
    }

    try {
      const request: UpdateSessionContextRequest = {
        sessionId: this.currentSession.session_id,
        context,
      };

      const updated = await invoke<CoreSession>('core_update_session_context', { request });

      this.currentSession = updated;
      this.notifySubscribers({ type: 'session_updated', data: updated });

      return { success: true, data: updated };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to update session context',
        code: 'SESSION_UPDATE_ERROR',
      };
    }
  }

