  /**
   * End current session (not yet implemented in Rust backend)
   */
  async endSession(): Promise<BridgeResponse<void>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: 'No active session',
        code: 'NO_SESSION',
      };
    }

    console.warn('[CoreBridge] endSession is not yet implemented in Rust backend');

    // Local cleanup
    this.notifySubscribers({
      type: 'session_ended',
      data: { sessionId: this.currentSession.session_id },
    });

    this.currentSession = null;
    this.messages = [];

    return { success: true, data: undefined };
  }

  // ============================================================================
  // Message Management
  // ============================================================================

  /**
   * Send a message through Core
   * Invokes: core_send_message
   */
  async sendMessage(
    message: Omit<CoreMessage, 'message_id' | 'timestamp'>
  ): Promise<BridgeResponse<CoreMessage>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: 'No active session',
        code: 'NO_SESSION',
      };
    }

    try {
      const request: SendMessageRequest = {
        sessionId: this.currentSession.session_id,
        fromAgent: message.from_agent,
        toAgent: message.to_agent,
        content: message.content,
        priority: message.priority,
      };

      const sent = await invoke<CoreMessage>('core_send_message', { request });

      this.messages.push(sent);
      this.notifySubscribers({ type: 'message_sent', data: sent });

      return { success: true, data: sent };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to send message',
        code: 'MESSAGE_SEND_ERROR',
      };
    }
  }

  /**
   * Get messages for current session
   * Invokes: core_get_messages
   */
  async getMessages(filter?: {
    fromAgent?: AgentId;
    toAgent?: AgentId | 'user';
    priority?: CoreMessage['priority'];
  }): Promise<BridgeResponse<CoreMessage[]>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: 'No active session',
        code: 'NO_SESSION',
      };
    }

    try {
      const request: GetMessagesRequest = {
        sessionId: this.currentSession.session_id,
        limit: 100, // Default limit
      };

      const messages = await invoke<CoreMessage[]>('core_get_messages', { request });

      // Apply client-side filtering if needed
      let filtered = messages;
      if (filter) {
        filtered = messages.filter(msg => {
          if (filter.fromAgent && msg.from_agent !== filter.fromAgent) return false;
          if (filter.toAgent && msg.to_agent !== filter.toAgent) return false;
          if (filter.priority && msg.priority !== filter.priority) return false;
          return true;
        });
      }

      this.messages = filtered;

      return { success: true, data: filtered };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to fetch messages',
        code: 'MESSAGE_FETCH_ERROR',
      };
    }
  }

