#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct VaultResponse {
    pub success: bool,
    pub message: String,
    pub data: Option<serde_json::Value>,
    pub entry_id: Option<String>,
}

pub struct SecureVaultFixed {
    db_manager: Arc<RwLock<Option<DatabaseManager>>>,
    key_manager: Arc<RwLock<Option<VaultKeyRotationManager>>>,
}

impl SecureVaultFixed {
    pub fn new() -> Self {
        Self {
            db_manager: Arc::new(RwLock::new(None)),
            key_manager: Arc::new(RwLock::new(None)),
        }
    }

    pub async fn initialize(&self, app_handle: &tauri::AppHandle) -> Result<(), String> {
        println!("[VAULT] Initializing secure vault with encryption...");

        let db_manager = DatabaseManager::initialize(app_handle).await?;

        {
            let mut db_guard = self.db_manager.write().await;
            *db_guard = Some(db_manager);
        }

        // Initialize key manager
        let app_dir = app_handle
            .path_resolver()
            .app_data_dir()
            .ok_or("Failed to get app data directory")?;

        let keys_db_path = app_dir.join("vault_keys.db");
        let key_manager = VaultKeyRotationManager::new(keys_db_path, None)
            .map_err(|e| format!("Failed to initialize key manager: {}", e))?;

        {
            let mut km_guard = self.key_manager.write().await;
            *km_guard = Some(key_manager);
        }

        println!("[VAULT] Secure vault initialized successfully with encryption");
        Ok(())
    }

    /// Get current encryption key from key manager
    async fn get_encryption_key(&self) -> Result<Vec<u8>, String> {
        let km_guard = self.key_manager.read().await;
        let key_manager = km_guard.as_ref()
            .ok_or("Key manager not initialized")?;

        let key_version = key_manager.get_current_key()
            .map_err(|e| format!("Failed to get encryption key: {}", e))?;

        Ok(key_version.key_data.clone())
    }

    /// Encrypt data with AES-256-GCM
    fn encrypt_data(&self, plaintext: &[u8], key: &[u8]) -> Result<(Vec<u8>, Vec<u8>), String> {
        let cipher = Aes256Gcm::new(GenericArray::from_slice(key));

        // Generate random nonce (96 bits for GCM)
        let mut nonce_bytes = [0u8; 12];
        OsRng.fill_bytes(&mut nonce_bytes);
        let nonce = Nonce::from_slice(&nonce_bytes);

        // Encrypt
        let ciphertext = cipher.encrypt(nonce, plaintext)
            .map_err(|e| format!("Encryption failed: {}", e))?;

        Ok((ciphertext, nonce_bytes.to_vec()))
    }

    /// Decrypt data with AES-256-GCM
    fn decrypt_data(&self, ciphertext: &[u8], nonce: &[u8], key: &[u8]) -> Result<Vec<u8>, String> {
        let cipher = Aes256Gcm::new(GenericArray::from_slice(key));
        let nonce = Nonce::from_slice(nonce);

        cipher.decrypt(nonce, ciphertext)
            .map_err(|e| format!("Decryption failed: {}", e))
    }

    pub async fn store_memory(&self, request: VaultRequest) -> Result<VaultResponse, String> {
        const MAX_USER_ID_LENGTH: usize = 128;
        const MAX_PERSONA_ID_LENGTH: usize = 128;
        const MAX_DATA_SIZE: usize = 10 * 1024 * 1024; // 10 MB

        println!("[VAULT] Store request: user_id={}, persona_id={}",
            request.user_id, request.persona_id);

        // Input validation
        if request.user_id.len() > MAX_USER_ID_LENGTH {
            return Err("User ID exceeds maximum length".to_string());
        }
        if request.persona_id.len() > MAX_PERSONA_ID_LENGTH {
            return Err("Persona ID exceeds maximum length".to_string());
        }

        // Authenticate user
        let user_id = auth::verify_token(&request.auth_token)?;
        if user_id != request.user_id {
            return Ok(VaultResponse {
                success: false,
                message: "Authentication failed: user mismatch".to_string(),
                data: None,
                entry_id: None,
            });
        }

        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref()
            .ok_or("Database not initialized")?;

        let entry_id = Uuid::new_v4().to_string();
        let now = Utc::now().timestamp();

        // Serialize data
        let data_json = serde_json::to_string(&request.data)
            .map_err(|e| format!("Failed to serialize data: {}", e))?;

        // Validate data size
        if data_json.len() > MAX_DATA_SIZE {
            return Err(format!(
                "Data size {} exceeds maximum allowed size {}",
                data_json.len(),
                MAX_DATA_SIZE
            ));
        }

        // Get encryption key
        let key = self.get_encryption_key().await?;

        // Get key version
        let km_guard = self.key_manager.read().await;
        let key_manager = km_guard.as_ref()
            .ok_or("Key manager not initialized")?;
        let key_version = key_manager.get_current_key()
            .map_err(|e| format!("Failed to get key version: {}", e))?;

        // Encrypt data
        let (ciphertext, nonce) = self.encrypt_data(data_json.as_bytes(), &key)?;

        // Store encrypted data
        sqlx::query(r#"
            INSERT INTO vault_entries
            (id, user_id, persona_id, encrypted_data, nonce, key_version, created_at, updated_at, schema_version)
            VALUES (?1, ?2, ?3, ?4, ?5, ?6, ?7, ?8, ?9)
        "#)
        .bind(&entry_id)
        .bind(&request.user_id)
        .bind(&request.persona_id)
        .bind(&ciphertext)
        .bind(&nonce)
        .bind(key_version.version)
        .bind(now)
        .bind(now)
        .bind("1.0")
        .execute(&db_manager.pool)
        .await
        .map_err(|e| format!("Failed to store memory: {}", e))?;

        // Log audit event
        self.log_audit_event(&user_id, "store", Some(&entry_id), None).await?;

        println!("[VAULT] Memory stored successfully (encrypted)");
        Ok(VaultResponse {
            success: true,
            message: "Memory stored successfully (encrypted)".to_string(),
            data: None,
            entry_id: Some(entry_id),
        })
    }

    pub async fn retrieve_memory(&self, user_id: String, persona_id: String, auth_token: String) -> Result<VaultResponse, String> {
        println!("[VAULT] Retrieve request: user_id={}, persona_id={}", user_id, persona_id);

        // Authenticate user
        let authenticated_user_id = auth::verify_token(&auth_token)?;
        if authenticated_user_id != user_id {
            return Ok(VaultResponse {
                success: false,
                message: "Authentication failed: user mismatch".to_string(),
                data: None,
                entry_id: None,
            });
        }

        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref()
            .ok_or("Database not initialized")?;

        let result = sqlx::query(r#"
            SELECT id, encrypted_data, nonce, key_version, created_at, updated_at
            FROM vault_entries
            WHERE user_id = ?1 AND persona_id = ?2
            ORDER BY updated_at DESC
            LIMIT 1
        "#)
        .bind(&user_id)
        .bind(&persona_id)
        .fetch_optional(&db_manager.pool)
        .await
        .map_err(|e| format!("Failed to retrieve memory: {}", e))?;

        let response = match result {
            Some(row) => {
                let encrypted_data: Vec<u8> = row.get("encrypted_data");
                let nonce: Vec<u8> = row.get("nonce");
                let key_version: i64 = row.get("key_version");

                // Get decryption key for this version
                let km_guard = self.key_manager.read().await;
                let key_manager = km_guard.as_ref()
                    .ok_or("Key manager not initialized")?;

                let key = if let Some(kv) = key_manager.get_key_by_version(key_version)
                    .map_err(|e| format!("Failed to get key version: {}", e))? {
                    kv.key_data
                } else {
                    return Err(format!("Encryption key version {} not found", key_version));
                };

                // Decrypt data
                let plaintext = self.decrypt_data(&encrypted_data, &nonce, &key)?;
                let data_json = String::from_utf8(plaintext)
                    .map_err(|e| format!("Invalid UTF-8 in decrypted data: {}", e))?;
                let data: serde_json::Value = serde_json::from_str(&data_json)
                    .map_err(|e| format!("Failed to parse decrypted data: {}", e))?;

                // Log audit event
                let entry_id: String = row.get("id");
                self.log_audit_event(&user_id, "retrieve", Some(&entry_id), None).await?;

                println!("[VAULT] Memory retrieved and decrypted successfully");
                VaultResponse {
                    success: true,
                    message: "Memory retrieved successfully (decrypted)".to_string(),
                    data: Some(data),
                    entry_id: Some(entry_id),
                }
            },
            None => VaultResponse {
                success: true,
                message: "No memory found for the specified user and persona".to_string(),
                data: None,
                entry_id: None,
            },
        };

        Ok(response)
    }

    async fn log_audit_event(&self, user_id: &str, action: &str, resource_id: Option<&str>, metadata: Option<serde_json::Value>) -> Result<(), String> {
        let db_guard = self.db_manager.read().await;
        let db_manager = db_guard.as_ref()
            .ok_or("Database not initialized")?;

        let event_id = Uuid::new_v4().to_string();
        let timestamp = Utc::now().timestamp();
        let metadata_json = metadata
            .map(|m| serde_json::to_string(&m).unwrap_or_default())
            .unwrap_or_default();

        sqlx::query(r#"
            INSERT INTO audit_log
            (id, user_id, action, resource_id, timestamp, metadata, schema_version)
            VALUES (?1, ?2, ?3, ?4, ?5, ?6, ?7)
        "#)
        .bind(&event_id)
        .bind(user_id)
        .bind(action)
        .bind(resource_id)
        .bind(timestamp)
        .bind(&metadata_json)
        .bind("1.0")
        .execute(&db_manager.pool)
        .await
        .map_err(|e| format!("Failed to log audit event: {}", e))?;

        Ok(())
    }
}
