/**
 * Hearthlink UI Component Library
 * Consistent, reusable components with unified styling and interactions
 */

import React, { useState, useRef, useEffect } from 'react';
import { HearthlinkColors, HearthlinkAnimations, HearthlinkComponents, useHearthlinkTheme } from '../../styles/HearthlinkDesignSystem';

// Button Component
interface HearthlinkButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'ghost' | 'danger';
  size?: 'sm' | 'md' | 'lg';
  loading?: boolean;
  icon?: React.ReactNode;
  children: React.ReactNode;
}

export const HearthlinkButton: React.FC<HearthlinkButtonProps> = ({
  variant = 'primary',
  size = 'md',
  loading = false,
  icon,
  children,
  disabled,
  className = '',
  style = {},
  ...props
}) => {
  const theme = useHearthlinkTheme();

  const getVariantStyles = () => {
    switch (variant) {
      case 'primary':
        return theme.components.button.primary;
      case 'secondary':
        return theme.components.button.secondary;
      case 'ghost':
        return theme.components.button.ghost;
      case 'danger':
        return {
          backgroundColor: `${HearthlinkColors.status.error}20`,
          borderColor: HearthlinkColors.status.error,
          color: HearthlinkColors.status.error,
          '&:hover:not(:disabled)': {
            backgroundColor: `${HearthlinkColors.status.error}30`,
            transform: 'translateY(-1px)',
            boxShadow: `0 4px 12px ${HearthlinkColors.status.error}30`
          }
        };
      default:
        return theme.components.button.primary;
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return { padding: '0.5rem 1rem', fontSize: '0.875rem' };
      case 'lg':
        return { padding: '1rem 2rem', fontSize: '1.125rem' };
      default:
        return { padding: '0.75rem 1.5rem', fontSize: '1rem' };
    }
  };

  const buttonStyle = {
    ...theme.components.button.base,
    ...getVariantStyles(),
    ...getSizeStyles(),
    ...style
  };

  return (
    <button
      className={`hlink-focus-visible ${className}`}
      style={buttonStyle}
      disabled={disabled || loading}
      {...props}
    >
      {loading ? (
        <div
          style={{
            width: '16px',
            height: '16px',
            border: '2px solid currentColor',
            borderTop: '2px solid transparent',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite'
          }}
        />
      ) : icon}
      {children}
    </button>
  );
};

// Card Component
interface HearthlinkCardProps {
  children: React.ReactNode;
  interactive?: boolean;
  hover?: boolean;
  className?: string;
  style?: React.CSSProperties;
  onClick?: () => void;
}

export const HearthlinkCard: React.FC<HearthlinkCardProps> = ({
  children,
  interactive = false,
  hover = true,
  className = '',
  style = {},
  onClick
}) => {
  const theme = useHearthlinkTheme();

  const cardStyle = {
    ...theme.components.card.base,
    ...(interactive ? theme.components.card.interactive : {}),
    ...(!hover ? { '&:hover': {} } : {}),
    ...style
  };

  return (
    <div
      className={`hlink-focus-visible ${className}`}
      style={cardStyle}
      onClick={onClick}
      role={interactive ? 'button' : undefined}
      tabIndex={interactive ? 0 : undefined}
    >
      {children}
    </div>
  );
};

// Input Component
interface HearthlinkInputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  label?: string;
  error?: string;
  icon?: React.ReactNode;
}

export const HearthlinkInput: React.FC<HearthlinkInputProps> = ({
  label,
  error,
  icon,
  className = '',
  style = {},
  ...props
}) => {
  const theme = useHearthlinkTheme();

  const inputStyle = {
    ...theme.components.input.base,
    ...style,
    ...(error ? { borderColor: HearthlinkColors.status.error } : {})
  };

  return (
    <div className={`hlink-input-group ${className}`}>
      {label && (
        <label
          style={{
            display: 'block',
            fontSize: '0.875rem',
            fontWeight: 500,
            color: HearthlinkColors.ui.textSecondary,
            marginBottom: '0.5rem'
          }}
        >
          {label}
        </label>
      )}
      <div style={{ position: 'relative' }}>
        {icon && (
          <div style={{
            position: 'absolute',
            left: '0.75rem',
            top: '50%',
            transform: 'translateY(-50%)',
            color: HearthlinkColors.ui.textMuted,
            pointerEvents: 'none'
          }}>
            {icon}
          </div>
        )}
        <input
          className="hlink-focus-visible"
          style={{
            ...inputStyle,
            ...(icon ? { paddingLeft: '2.5rem' } : {})
          }}
          {...props}
        />
      </div>
      {error && (
        <div style={{
          fontSize: '0.75rem',
          color: HearthlinkColors.status.error,
          marginTop: '0.25rem'
        }}>
          {error}
        </div>
      )}
    </div>
  );
};

// Textarea Component
interface HearthlinkTextareaProps extends React.TextareaHTMLAttributes<HTMLTextAreaElement> {
  label?: string;
  error?: string;
}

export const HearthlinkTextarea: React.FC<HearthlinkTextareaProps> = ({
  label,
  error,
  className = '',
  style = {},
  ...props
}) => {
  const theme = useHearthlinkTheme();

  const textareaStyle = {
    ...theme.components.input.base,
    minHeight: '120px',
    resize: 'vertical' as const,
    ...style,
    ...(error ? { borderColor: HearthlinkColors.status.error } : {})
  };

  return (
    <div className={`hlink-textarea-group ${className}`}>
      {label && (
        <label
          style={{
            display: 'block',
            fontSize: '0.875rem',
            fontWeight: 500,
            color: HearthlinkColors.ui.textSecondary,
            marginBottom: '0.5rem'
          }}
        >
          {label}
        </label>
      )}
      <textarea
        className="hlink-focus-visible"
        style={textareaStyle}
        {...props}
      />
      {error && (
        <div style={{
          fontSize: '0.75rem',
          color: HearthlinkColors.status.error,
          marginTop: '0.25rem'
        }}>
          {error}
        </div>
      )}
    </div>
  );
};

// Modal Component
interface HearthlinkModalProps {
  isOpen: boolean;
  onClose: () => void;
  title?: string;
  children: React.ReactNode;
  size?: 'sm' | 'md' | 'lg' | 'xl';
}

export const HearthlinkModal: React.FC<HearthlinkModalProps> = ({
  isOpen,
  onClose,
  title,
  children,
  size = 'md'
}) => {
  const theme = useHearthlinkTheme();
  const modalRef = useRef<HTMLDivElement>(null);

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return { maxWidth: '400px' };
      case 'lg':
        return { maxWidth: '700px' };
      case 'xl':
        return { maxWidth: '900px' };
      default:
        return { maxWidth: '500px' };
    }
  };

  // Handle ESC key and outside clicks
  useEffect(() => {
    if (!isOpen) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') onClose();
    };

    const handleClickOutside = (e: MouseEvent) => {
      if (modalRef.current && !modalRef.current.contains(e.target as Node)) {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    document.addEventListener('mousedown', handleClickOutside);
    document.body.style.overflow = 'hidden';

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.removeEventListener('mousedown', handleClickOutside);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  if (!isOpen) return null;

  return (
    <div style={theme.components.modal.overlay}>
      <div
        ref={modalRef}
        style={{
          ...theme.components.modal.content,
          ...getSizeStyles()
        }}
      >
        {title && (
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '1.5rem',
            paddingBottom: '1rem',
            borderBottom: `1px solid ${HearthlinkColors.ui.borderSecondary}`
          }}>
            <h2 style={{
              margin: 0,
              fontSize: '1.25rem',
              fontWeight: 600,
              color: HearthlinkColors.ui.textPrimary
            }}>
              {title}
            </h2>
            <button
              onClick={onClose}
              style={{
                background: 'none',
                border: 'none',
                color: HearthlinkColors.ui.textSecondary,
                cursor: 'pointer',
                fontSize: '1.5rem',
                padding: '0.25rem',
                transition: HearthlinkAnimations.transition.colors
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.color = HearthlinkColors.ui.textPrimary;
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.color = HearthlinkColors.ui.textSecondary;
              }}
            >
              +�
            </button>
          </div>
        )}
        {children}
      </div>
    </div>
  );
};

// Badge Component
interface HearthlinkBadgeProps {
  children: React.ReactNode;
  variant?: 'default' | 'success' | 'warning' | 'error' | 'info';
  size?: 'sm' | 'md';
}

export const HearthlinkBadge: React.FC<HearthlinkBadgeProps> = ({
  children,
  variant = 'default',
  size = 'md'
}) => {
  const getVariantStyles = () => {
    switch (variant) {
      case 'success':
        return {
          backgroundColor: `${HearthlinkColors.status.success}20`,
          color: HearthlinkColors.status.success,
          border: `1px solid ${HearthlinkColors.status.success}50`
        };
      case 'warning':
        return {
          backgroundColor: `${HearthlinkColors.status.warning}20`,
          color: HearthlinkColors.status.warning,
          border: `1px solid ${HearthlinkColors.status.warning}50`
        };
      case 'error':
        return {
          backgroundColor: `${HearthlinkColors.status.error}20`,
          color: HearthlinkColors.status.error,
          border: `1px solid ${HearthlinkColors.status.error}50`
        };
      case 'info':
        return {
          backgroundColor: `${HearthlinkColors.status.info}20`,
          color: HearthlinkColors.status.info,
          border: `1px solid ${HearthlinkColors.status.info}50`
        };
      default:
        return {
          backgroundColor: `${HearthlinkColors.ui.textMuted}20`,
          color: HearthlinkColors.ui.textSecondary,
          border: `1px solid ${HearthlinkColors.ui.textMuted}50`
        };
    }
  };

  const getSizeStyles = () => {
    switch (size) {
      case 'sm':
        return {
          fontSize: '0.75rem',
          padding: '0.25rem 0.5rem'
        };
      default:
        return {
          fontSize: '0.875rem',
          padding: '0.375rem 0.75rem'
        };
    }
  };

  return (
    <span
      style={{
        display: 'inline-flex',
        alignItems: 'center',
        borderRadius: '0.375rem',
        fontWeight: 500,
        transition: HearthlinkAnimations.transition.all,
        ...getVariantStyles(),
        ...getSizeStyles()
      }}
    >
      {children}
    </span>
  );
};

// Loading Spinner Component
interface HearthlinkSpinnerProps {
  size?: 'sm' | 'md' | 'lg';
  color?: string;
}

export const HearthlinkSpinner: React.FC<HearthlinkSpinnerProps> = ({
  size = 'md',
  color = HearthlinkColors.starcraft.blue
}) => {
  const getSizeValue = () => {
    switch (size) {
      case 'sm': return '16px';
      case 'lg': return '32px';
      default: return '24px';
    }
  };

  return (
    <div
      style={{
        width: getSizeValue(),
        height: getSizeValue(),
        border: '2px solid transparent',
        borderTop: `2px solid ${color}`,
        borderRadius: '50%',
        animation: 'spin 1s linear infinite'
      }}
    />
  );
};

// Progress Bar Component
interface HearthlinkProgressProps {
  value: number;
  max?: number;
  color?: string;
  showLabel?: boolean;
  size?: 'sm' | 'md' | 'lg';
}

export const HearthlinkProgress: React.FC<HearthlinkProgressProps> = ({
  value,
  max = 100,
  color = HearthlinkColors.starcraft.blue,
  showLabel = false,
  size = 'md'
}) => {
  const percentage = Math.min((value / max) * 100, 100);

  const getHeightValue = () => {
    switch (size) {
      case 'sm': return '4px';
      case 'lg': return '12px';
      default: return '8px';
    }
  };

  return (
    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
      <div
        style={{
          flex: 1,
          backgroundColor: `${HearthlinkColors.starcraft.dark}80`,
          borderRadius: '0.25rem',
          overflow: 'hidden',
          height: getHeightValue(),
          border: `1px solid ${HearthlinkColors.ui.borderSecondary}`
        }}
      >
        <div
          style={{
            height: '100%',
            backgroundColor: color,
            width: `${percentage}%`,
            transition: HearthlinkAnimations.transition.all,
            boxShadow: `0 0 8px ${color}50`
          }}
        />
      </div>
      {showLabel && (
        <span style={{
          fontSize: '0.875rem',
          color: HearthlinkColors.ui.textSecondary,
          minWidth: '3rem',
          textAlign: 'right' as const
        }}>
          {Math.round(percentage)}%
        </span>
      )}
    </div>
  );
};

// Export all components
export const HearthlinkUI = {
  Button: HearthlinkButton,
  Card: HearthlinkCard,
  Input: HearthlinkInput,
  Textarea: HearthlinkTextarea,
  Modal: HearthlinkModal,
  Badge: HearthlinkBadge,
  Spinner: HearthlinkSpinner,
  Progress: HearthlinkProgress
};

export default HearthlinkUI;

