(function () {
  const STORAGE_KEY = 'uiRecoveryTheme';
  const DEFAULT_THEME = 'unified';

  const applyTheme = (theme) => {
    document.documentElement.setAttribute('data-theme', theme);
    document.querySelectorAll('[data-theme-toggle]').forEach((btn) => {
      btn.textContent = theme === 'legacy' ? 'Switch to Unified theme' : 'Switch to Legacy theme';
    });
  };

  document.addEventListener('DOMContentLoaded', () => {
    const stored = window.localStorage ? window.localStorage.getItem(STORAGE_KEY) : null;
    const startingTheme = stored || DEFAULT_THEME;
    applyTheme(startingTheme);

    document.querySelectorAll('[data-theme-toggle]').forEach((btn) => {
      btn.addEventListener('click', () => {
        const nextTheme = document.documentElement.getAttribute('data-theme') === 'legacy' ? 'unified' : 'legacy';
        if (window.localStorage) {
          window.localStorage.setItem(STORAGE_KEY, nextTheme);
        }
        applyTheme(nextTheme);
      });
    });
  });
})();
