document.addEventListener('DOMContentLoaded', () => {
  const params = new URLSearchParams(window.location.search);
  const moduleKey = params.get('name');
  const module = window.uiRecoveryModules?.[moduleKey];
  const main = document.querySelector('main');
  if (!module) {
    if (main) {
      main.innerHTML = `
        <section style="text-align:center; padding: 4rem 1rem;">
          <p style="font-size:1rem; color:var(--text-secondary);">Unknown module &ldquo;${moduleKey || 'none'}&rdquo;.</p>
          <a href="index.html" style="font-weight:600; color:var(--accent-primary);">Return to explorer</a>
        </section>`;
    }
    return;
  }

  document.getElementById('moduleLabel').textContent = module.key.replace(/([A-Z])/g, ' $1').trim();
  document.getElementById('moduleHeadline').textContent = module.label;
  document.getElementById('moduleDescription').textContent = module.description;
  document.getElementById('moduleStatus').textContent = module.status;
  document.getElementById('modulePalette').textContent = module.palette;

  const layoutList = document.getElementById('layoutList');
  module.layout.forEach((item) => {
    const li = document.createElement('li');
    li.textContent = item;
    layoutList.appendChild(li);
  });

  const highlights = document.getElementById('moduleHighlights');
  module.highlights.forEach((highlight) => {
    const card = document.createElement('div');
    card.className = 'highlight-card';
    const title = document.createElement('h3');
    title.textContent = highlight.title;
    const detail = document.createElement('p');
    detail.textContent = highlight.detail;
    card.appendChild(title);
    card.appendChild(detail);
    highlights.appendChild(card);
  });

  const moduleActions = document.getElementById('moduleActions');
  module.actions.forEach((action) => {
    const card = document.createElement('div');
    card.className = 'action-card';
    const title = document.createElement('h3');
    title.textContent = action.label;
    const detail = document.createElement('p');
    detail.textContent = action.detail || 'Simulates the user interaction placeholder.';
    const button = document.createElement('button');
    button.textContent = action.label;
    if (action.tone) {
      button.classList.add(action.tone);
    }
    card.appendChild(title);
    card.appendChild(detail);
    card.appendChild(button);
    moduleActions.appendChild(card);
  });

  const notes = document.getElementById('moduleNotes');
  if (module.notes) {
    module.notes.forEach((note) => {
      const li = document.createElement('li');
      li.textContent = note;
      notes.appendChild(li);
    });
  }

  const metricsContainer = document.getElementById('moduleMetrics');
  module.metrics.forEach((metric) => {
    const metricCard = document.createElement('div');
    metricCard.className = 'metric';
    const value = document.createElement('strong');
    value.textContent = metric.value;
    const label = document.createElement('span');
    label.textContent = metric.label;
    const detail = document.createElement('small');
    detail.textContent = metric.detail || '';
    metricCard.appendChild(value);
    metricCard.appendChild(label);
    if (metric.detail) {
      metricCard.appendChild(detail);
    }
    metricsContainer.appendChild(metricCard);
  });
});
