  document.getElementById('modulePalette').textContent = module.palette;

  const layoutList = document.getElementById('layoutList');
  (module.layout || []).forEach((item) => {
    const li = document.createElement('li');
    li.textContent = item;
    layoutList.appendChild(li);
  });

  const canvasContainer = document.getElementById('moduleCanvas');
  if (canvasContainer) {
    if (module.layout && module.layout.length) {
      module.layout.forEach((item, index) => {
        const panel = document.createElement('div');
        panel.className = 'canvas-panel';
        const title = document.createElement('p');
        title.className = 'panel-title';
        title.textContent = `Canvas ${index + 1}`;
        const lines = document.createElement('div');
        lines.className = 'panel-lines';
        const lineCount = 3 + (index % 2);
        for (let i = 0; i < lineCount; i += 1) {
          const line = document.createElement('span');
          lines.appendChild(line);
        }
        const desc = document.createElement('p');
        desc.textContent = item;
        panel.appendChild(title);
        panel.appendChild(lines);
        panel.appendChild(desc);
        canvasContainer.appendChild(panel);
      });
    } else {
      canvasContainer.textContent = 'Canvas assets pending';
    }
  }

  const highlights = document.getElementById('moduleHighlights');
  (module.highlights || []).forEach((highlight) => {
    const card = document.createElement('div');
    card.className = 'highlight-card';
    const title = document.createElement('h3');
    title.textContent = highlight.title;
    const detail = document.createElement('p');
    detail.textContent = highlight.detail;
    card.appendChild(title);
    card.appendChild(detail);
    highlights.appendChild(card);
  });

  const moduleActions = document.getElementById('moduleActions');
  (module.actions || []).forEach((action) => {
    const card = document.createElement('div');
    card.className = 'action-card';
    const title = document.createElement('h3');
    title.textContent = action.label;
    const detail = document.createElement('p');
