import React, { useState, useRef, useEffect } from 'react';
import { Send, Settings, User, Triangle, Siren } from 'lucide-react';

interface Message {
  id: string;
  sender: 'SYSTEM' | 'ALDEN' | 'USER' | 'ALERT';
  text: string;
  timestamp: string;
  type: 'info' | 'success' | 'warning' | 'error' | 'chat';
}

export const GlobalCommsStream: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([
    { id: '1', sender: 'SYSTEM', text: 'FIREWALL INTEGRITY CHECK PASSED.', timestamp: '22:43', type: 'success' },
    { id: '2', sender: 'USER', text: "ALDEN, INITIATE 'PROJECT CHIMERA' SYNC.", timestamp: '22:44', type: 'chat' },
    { id: '3', sender: 'ALDEN', text: 'ACKNOWLEDGED. INITIATING SYNC SEQUENCE FOR PROJECT CHIMERA. ESTIMATED TIME: 3 MINS.', timestamp: '22:44', type: 'info' },
    { id: '4', sender: 'ALERT', text: 'EXTERNAL CONNECTION ATTEMPT DETECTED ON PORT 8080.', timestamp: '22:45', type: 'error' },
  ]);

  const [inputValue, setInputValue] = useState('');
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputValue.trim()) return;
    const newMessage: Message = {
      id: Date.now().toString(),
      sender: 'USER',
      text: inputValue.toUpperCase(),
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: false }),
      type: 'chat',
    };
    setMessages((prev) => [...prev, newMessage]);
    setInputValue('');
    setTimeout(() => {
      const response: Message = {
        id: (Date.now() + 1).toString(),
        sender: 'ALDEN',
        text: 'COMMAND RECEIVED. PROCESSING...',
        timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: false }),
        type: 'info',
      };
      setMessages((prev) => [...prev, response]);
    }, 1000);
  };

  return (
    <div className="flex flex-col h-full w-full font-inter text-xs relative overflow-hidden bg-[#050a14] border-2 border-amber-500/60 rounded-xl shadow-[0_0_20px_rgba(245,158,11,0.2)]">
       {/* Header */}
       <div className="h-12 border-b border-amber-500/40 flex items-center justify-center bg-amber-950/20">
         <h3 className="text-amber-400 font-orbitron-force tracking-[0.2em] font-bold text-sm">GLOBAL COMMS STREAM</h3>
       </div>

       {/* Stream */}
       <div ref={scrollRef} className="flex-1 overflow-y-auto p-5 space-y-6 relative z-10">
         {messages.map((msg) => (
           <div key={msg.id} className="flex flex-col space-y-1">
             <div className="flex items-center space-x-2">
               {msg.sender === 'SYSTEM' && <Settings className="text-cyan-400 w-4 h-4" />}
               {msg.sender === 'USER' && <User className="text-slate-400 w-4 h-4" />}
               {msg.sender === 'ALDEN' && <Triangle className="text-amber-400 w-4 h-4 rotate-180" />}
               {msg.sender === 'ALERT' && <Siren className="text-red-500 w-4 h-4" />}

               <span className="text-[10px] font-mono text-slate-500">[{msg.timestamp}]</span>

               <span className={`text-xs font-bold tracking-wider ${
                 msg.sender === 'SYSTEM' ? 'text-cyan-400' :
                 msg.sender === 'USER' ? 'text-slate-300' :
                 msg.sender === 'ALDEN' ? 'text-amber-400' : 'text-red-500'
               }`}>
                 {msg.sender} &gt;
               </span>
             </div>

             <div className={`pl-6 text-sm font-medium leading-relaxed ${
               msg.sender === 'SYSTEM' ? 'text-cyan-200' :
               msg.sender === 'USER' ? 'text-white' :
               msg.sender === 'ALDEN' ? 'text-amber-100' : 'text-red-400'
             }`}>
               {msg.text}
             </div>
           </div>
         ))}
       </div>

       {/* Input Area */}
       <div className="p-4 bg-[#020408] border-t border-amber-500/30">
         <form onSubmit={handleSendMessage} className="relative">
           <input
             type="text"
             value={inputValue}
             onChange={(e) => setInputValue(e.target.value)}
             placeholder="BROADCAST MESSAGE >"
             className="w-full h-10 bg-slate-900/50 border border-slate-700 text-slate-300 px-4 
                      focus:border-amber-500 focus:text-amber-400 focus:outline-none 
                      transition-all placeholder-slate-600 uppercase text-xs tracking-widest"
           />
         </form>
       </div>
    </div>
  );
};
