import React, { useState, useMemo, useCallback } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { format, startOfMonth, endOfMonth, startOfWeek, endOfWeek, eachDayOfInterval, getDay, isSameDay, addMonths, subMonths, addWeeks, subWeeks, addDays, subDays, parseISO } from 'date-fns';
import { CalendarEvent } from '../../types/agents';

type CalendarViewMode = 'day' | 'week' | 'month';

interface CalendarViewProps {
  events: CalendarEvent[];
  onEventClick: (event: CalendarEvent) => void;
}

export const CalendarView: React.FC<CalendarViewProps> = ({ events, onEventClick }) => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [viewMode, setViewMode] = useState<CalendarViewMode>('month');
  const [selectedAccounts, setSelectedAccounts] = useState<string[]>(['Primary Account']);
  const accountOptions = ['Primary Account', 'Work', 'Personal', 'All Accounts'];
  const [showAccountMenu, setShowAccountMenu] = useState(false);

  // Navigation functions
  const navigatePrevious = () => {
    switch (viewMode) {
      case 'day': setCurrentDate(subDays(currentDate, 1)); break;
      case 'week': setCurrentDate(subWeeks(currentDate, 1)); break;
      case 'month': setCurrentDate(subMonths(currentDate, 1)); break;
    }
  };

  const navigateNext = () => {
    switch (viewMode) {
      case 'day': setCurrentDate(addDays(currentDate, 1)); break;
      case 'week': setCurrentDate(addWeeks(currentDate, 1)); break;
      case 'month': setCurrentDate(addMonths(currentDate, 1)); break;
    }
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  const toggleAccount = (value: string) => {
    setSelectedAccounts((prev) => prev.includes(value) ? prev.filter(v => v !== value) : [...prev, value]);
  };

  const getTitle = () => {
    switch (viewMode) {
      case 'day': return format(currentDate, 'EEEE, MMMM d, yyyy');
      case 'week':
        const weekStart = startOfWeek(currentDate);
        const weekEnd = endOfWeek(currentDate);
        return `${format(weekStart, 'MMM d')} - ${format(weekEnd, 'MMM d, yyyy')}`;
      case 'month': return format(currentDate, 'MMMM yyyy');
    }
  };

  const getEventsForDate = useCallback((date: Date) => {
    return events.filter((event) => isSameDay(parseISO(event.start_time), date));
  }, [events]);

  const dayViewEvents = useMemo(() => {
    return getEventsForDate(currentDate).sort((a, b) => new Date(a.start_time).getTime() - new Date(b.start_time).getTime());
  }, [currentDate, getEventsForDate]);

  // Day View component (Preserved)
  const renderDayView = () => {
    const hours = Array.from({ length: 24 }, (_, i) => i);
    return (
      <div className="p-4">
        <div className="space-y-1">
          {hours.map((hour) => {
            const hourEvents = dayViewEvents.filter((event) => new Date(event.start_time).getHours() === hour);
            return (
              <div key={hour} className="flex border-b border-slate-700/50 min-h-[60px]">
                <div className="w-16 text-xs text-muted-foreground py-2 pr-2 text-right">{format(new Date().setHours(hour, 0, 0, 0), 'h a')}</div>
                <div className="flex-1 py-1 pl-2">
                  {hourEvents.map((event) => (
                    <button key={event.id} onClick={() => onEventClick(event)} className="w-full bg-cyan-800/50 text-white px-2 py-1 rounded text-sm mb-1 cursor-pointer hover:bg-cyan-700/50 transition-colors text-left">
                      <div className="font-medium truncate">{event.title}</div>
                      <div className="text-xs text-cyan-300">{format(parseISO(event.start_time), 'h:mm a')} - {format(parseISO(event.end_time), 'h:mm a')}</div>
                    </button>
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  // Week View component (Preserved)
  const renderWeekView = () => {
    const weekStart = startOfWeek(currentDate);
    const weekEnd = endOfWeek(currentDate);
    const weekDays = eachDayOfInterval({ start: weekStart, end: weekEnd });
    const hours = Array.from({ length: 24 }, (_, i) => i);
    return (
      <div className="p-4 overflow-x-auto">
        <div className="flex border-b border-slate-700/50 sticky top-0 bg-[var(--background)] z-10">
          <div className="w-16 shrink-0" />
          {weekDays.map((day) => (
            <div key={day.toString()} className={`flex-1 text-center py-2 min-w-[100px] ${isSameDay(day, new Date()) ? 'bg-cyan-900/30' : ''}`}>
              <div className="text-xs text-muted-foreground">{format(day, 'EEE')}</div>
              <div className={`text-lg font-semibold ${isSameDay(day, new Date()) ? 'text-cyan-400' : ''}`}>{format(day, 'd')}</div>
            </div>
          ))}
        </div>
        <div className="max-h-[600px] overflow-y-auto">
          {hours.map((hour) => (
            <div key={hour} className="flex border-b border-slate-700/30 min-h-[50px]">
              <div className="w-16 shrink-0 text-xs text-muted-foreground py-1 pr-2 text-right">{format(new Date().setHours(hour, 0, 0, 0), 'h a')}</div>
              {weekDays.map((day) => {
                const dayEvents = getEventsForDate(day).filter((event) => new Date(event.start_time).getHours() === hour);
                return (
                  <div key={day.toString()} className={`flex-1 min-w-[100px] border-l border-slate-700/30 p-1 ${isSameDay(day, new Date()) ? 'bg-cyan-900/10' : ''}`}>
                    {dayEvents.map((event) => (
                      <button key={event.id} onClick={() => onEventClick(event)} className="w-full bg-cyan-800/50 text-white text-xs px-1 py-0.5 rounded mb-1 cursor-pointer hover:bg-cyan-700/50 truncate text-left" title={event.title}>
                        {event.title}
                      </button>
                    ))}
                  </div>
                );
              })}
            </div>
          ))}
        </div>
      </div>
    );
  };

  // Month View component (UPDATED TO MOCKUP)
  const renderMonthView = () => {
    const firstDayOfMonth = startOfMonth(currentDate);
    const lastDayOfMonth = endOfMonth(currentDate);
    const daysInMonth = eachDayOfInterval({ start: firstDayOfMonth, end: lastDayOfMonth });
    const startingDayIndex = getDay(firstDayOfMonth);
    const blanks = Array.from({ length: startingDayIndex });

    return (
      <div className="p-4 bg-[#050a14] border-2 border-cyan-500/30 rounded-xl shadow-[0_0_20px_rgba(6,182,212,0.1)]">
        {/* Header */}
        <div className="flex justify-between items-end mb-6 border-b-2 border-cyan-500/50 pb-4">
          <div className="flex items-center gap-4">
            <h2 className="text-3xl text-cyan-400 font-orbitron-force tracking-widest uppercase font-bold shadow-cyan-glow">
              {format(currentDate, 'MMMM yyyy')}
            </h2>
            <div className="flex gap-1">
              <button onClick={navigatePrevious} className="p-1 text-cyan-500 hover:text-white"><ChevronLeft /></button>
              <button onClick={navigateNext} className="p-1 text-cyan-500 hover:text-white"><ChevronRight /></button>
            </div>
          </div>
          <div className="flex gap-1">
            {['DAY', 'WEEK', 'MONTH'].map(m => (
              <button 
                key={m} 
                onClick={() => setViewMode(m.toLowerCase() as CalendarViewMode)}
                className={`px-4 py-1.5 text-[10px] font-bold border tracking-wider transition-all ${
                  viewMode === m.toLowerCase() 
                  ? 'bg-cyan-500 text-black border-cyan-500 shadow-[0_0_10px_rgba(6,182,212,0.6)]' 
                  : 'text-cyan-500 border-cyan-900 hover:border-cyan-500'
                }`}
              >
                {m}
              </button>
            ))}
          </div>
        </div>

        {/* Grid */}
        <div className="border border-cyan-500/20 bg-[#020408] p-2 rounded-lg">
          <div className="grid grid-cols-7 mb-2 border-b border-cyan-500/20 pb-2">
            {['MON', 'TUE', 'WED', 'THU', 'FRI', 'SAT', 'SUN'].map((day) => (
              <div key={day} className="text-center text-xs text-slate-500 font-orbitron-force tracking-widest">
                {day}
              </div>
            ))}
          </div>

          <div className="grid grid-cols-7 gap-1.5">
            {blanks.map((_, i) => <div key={`blank-${i}`} className="aspect-square" />)}
            {daysInMonth.map((day) => {
               const isToday = isSameDay(day, new Date());
               return (
                 <div 
                   key={day.toString()}
                   className={`
                     relative p-2 aspect-square border transition-all flex flex-col justify-between group cursor-pointer
                     ${isToday 
                       ? 'bg-cyan-950/40 border-cyan-400 ring-1 ring-cyan-400 shadow-[inset_0_0_20px_rgba(6,182,212,0.2)]' 
                       : 'bg-[#081020] border-cyan-900/30 hover:border-cyan-500/50 hover:bg-cyan-900/10'}
                   `}
                 >
                   <span className={`text-sm font-bold font-orbitron-force ${isToday ? 'text-cyan-400' : 'text-slate-600 group-hover:text-cyan-200'}`}>
                     {format(day, 'd')}
                   </span>

                   <div className="space-y-1">
                     {getEventsForDate(day).slice(0, 3).map(event => (
                       <div key={event.id} className="h-1.5 w-full rounded-sm bg-cyan-500 shadow-[0_0_5px_rgba(6,182,212,0.5)]" />
                     ))}
                   </div>
                 </div>
               );
            })}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="h-full">
      {viewMode === 'day' && renderDayView()}
      {viewMode === 'week' && renderWeekView()}
      {viewMode === 'month' && renderMonthView()}
    </div>
  );
};
