         // Context switching logic would go here. 
         // For this implementation, we will just send the message to Alden.
         // Future: await mimicBridgeRef.current.sendMessage(activePersonaId, trimmed);
      }

      const response = await aldenBridgeRef.current.sendChatMessage(messageToSend);
      if (response.success && response.data) {
        setConversationHistory((prev) => [...prev, normalizeChatMessage(response.data)]);
      } else if (!response.success) {
        setErrorMessage(response.error);
      }
    } catch (error: unknown) {
      setErrorMessage(error instanceof Error ? error.message : 'Failed to send message');
    } finally {
      setIsSending(false);
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const getActivePersonaName = () => {
