export class MimicBridge extends BaseBridge<MimicProps> {
  private activePersona: MimicPersona | null = null;
  private availablePersonas: MimicPersona[] = [];
  private adaptations: MimicAdaptation[] = [];
  private dataLoaded = false;

  constructor(config: Partial<BridgeConfig> & { userId: string }) {
    super({
      agentId: 'mimic',
      ...config,
    });
  }

  /**
   * Initialize Mimic bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log('[MimicBridge.onInitialize] START - Initializing Mimic bridge');

    // Initialize with empty state - data will be loaded lazily
    this.activePersona = null;
    this.availablePersonas = [];
    this.adaptations = [];
    this.dataLoaded = false;

    console.log('[MimicBridge.onInitialize] END - Initialized successfully (data will load lazily)');
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.activePersona = null;
    this.availablePersonas = [];
    this.adaptations = [];
    this.dataLoaded = false;
  }

  /**
   * Get props for Mimic React component
   *
   * Performs lazy loading of persona data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<MimicProps> {
    console.log('[MimicBridge.getProps] START - Getting props for React component');

    // Lazy load persona data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
      console.log('[MimicBridge.getProps] Lazy loading persona data (first call after mount)');
      try {
        const request = { user_id: this.userId };

        // Load all available personas
        this.availablePersonas = await this.invokeCommand<MimicPersona[]>('mimic_get_personas', { request });

        // Load active persona
        const activePersonaData = await this.invokeCommand<{ persona: MimicPersona }>('mimic_get_active_persona', { request });
        this.activePersona = activePersonaData.persona || null;

