+
+const ensureAldenParticipant = (list: Participant[]): Participant[] => {
+  const hasAlden = list.some((participant) => participant.id === 'alden');
+  if (hasAlden) return list;
+  return [
+    {
+      id: 'alden',
+      type: 'persona',
+      displayName: 'Alden',
+      role: 'System Core',
+      status: 'idle',
+      avatarUrl: '/assets/Alden.png',
+    },
+    ...list,
+  ];
+};
+
+const normalizeChatMessage = (entry: any): NormalizedChatMessage => {
+  const id =
+    entry.messageId ||
+    entry.message_id ||
+    entry.id ||
+    `${entry.userId || entry.user_id || 'chat'}-${Date.now()}-${Math.random().toString(36).slice(2)}`;
+
+  const rawRole = String(entry.role || entry.sender || entry.from_agent || entry.author || 'assistant').toLowerCase();
+  const role: NormalizedChatMessage['role'] =
+    rawRole === 'alden' ? 'assistant' : (rawRole as NormalizedChatMessage['role']);
+
+  const content = entry.content ?? entry.message ?? entry.response ?? '';
+  const createdAt =
+    (typeof entry.createdAt === 'string' && entry.createdAt) ||
+    (typeof entry.timestamp === 'string' && entry.timestamp) ||
+    (typeof entry.created_at === 'string' && entry.created_at) ||
+    new Date().toISOString();
+  const sessionId =
+    (typeof entry.sessionId === 'string' && entry.sessionId) ||
+    (typeof entry.session_id === 'string' && entry.session_id) ||
+    'session-default';
+
+  return {
+    id: String(id),
+    role: role === 'user' || role === 'assistant' ? role : 'system',
+    content,
+    createdAt,
+    sessionId,
+    personaId: entry.personaId || entry.persona_id || entry.agent_id || 'alden',
+  };
+};
+
+const createLocalMessage = (content: string, sessionId: string, personaId: string): NormalizedChatMessage => ({
+  id: `local-${Date.now()}-${Math.random().toString(36).slice(2)}`,
+  role: 'user',
+  content,
+  createdAt: new Date().toISOString(),
+  sessionId,
+  personaId,
