{"command":"@'
import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { Mic, MicOff, Send, Volume2, VolumeX, Users, ChevronLeft, MessageSquare, LogOut, Sparkles, Download } from 'lucide-react';
import { AldenBridge } from '../../bridges/AldenBridge';
import { MimicBridge } from '../../bridges/MimicBridge';
import type { MimicPersona, AuditEvent } from '../../types/agents';

type PersonaStatus = 'idle' | 'thinking' | 'speaking';

type Participant = {
  id: string;
  type: 'persona' | 'external' | 'system';
  role?: string;
  displayName?: string;
  avatarUrl?: string;
  status?: PersonaStatus;
};

type SessionEvent = {
  event_id: string;
  event: 'join' | 'leave' | 'message' | 'response' | 'system';
  agent_id: string;
  content?: string;
  timestamp: string;
  persona_id?: string;
  visibility?: 'live' | 'log-only';
  metadata?: Record<string, unknown>;
};

type BreakoutRoom = {
  breakout_id: string;
  topic: string;
  participants: string[];
  log: SessionEvent[];
};

type LiveFeedSettings = {
  verbosity: 'minimal' | 'default' | 'verbose';
  hidden_responses?: string[];
};

type CoreSessionProps = {
  roomId: string;
  topic: string;
  participants: Participant[];
  sessionLog: SessionEvent[];
  breakouts: BreakoutRoom[];
  liveFeedSettings: LiveFeedSettings;
  onParticipantChange: (participants: Participant[]) => void;
  onBreakoutCreate: (breakout: BreakoutRoom) => void;
  onLogExport: () => void;
  onSessionEnd: () => void;
  auditLog: AuditEvent[];
};

type AvatarStreamState = {
  personaId: string;
  isSpeaking?: boolean;
  isThinking?: boolean;
  lastActiveAt?: number;
};

type ExpandedChatProps = CoreSessionProps & {
  userId?: string;
  onClose?: () => void;
  avatarStream?: AvatarStreamState[];
};

type NormalizedChatMessage = {
  id: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  createdAt: string;
  sessionId: string;
  personaId?: string;
};

const ensureAldenParticipant = (list: Participant[]): Participant[] => {
  const hasAlden = list.some((participant) => participant.id === 'alden');
  if (hasAlden) return list;
  return [
    {
      id: 'alden',
      type: 'persona',
      displayName: 'Alden',
      role: 'System Core',
      status: 'idle',
      avatarUrl: '/assets/Alden.png',
    },
    ...list,
  ];
};

const normalizeChatMessage = (entry: any): NormalizedChatMessage => {
  const id =
    entry.messageId ||
    entry.message_id ||
    entry.id ||
    `${entry.userId || entry.user_id || 'chat'}-${Date.now()}-${Math.random().toString(36).slice(2)}`;

  const rawRole = String(entry.role || entry.sender || entry.from_agent || entry.author || 'assistant').toLowerCase();
  const role: NormalizedChatMessage['role'] =
    rawRole === 'alden' ? 'assistant' : (rawRole as NormalizedChatMessage['role']);

  const content = entry.content ?? entry.message ?? entry.response ?? '';
  const createdAt =
    (typeof entry.createdAt === 'string' && entry.createdAt) ||
    (typeof entry.timestamp === 'string' && entry.timestamp) ||
    (typeof entry.created_at === 'string' && entry.created_at) ||
    new Date().toISOString();
  const sessionId =
    (typeof entry.sessionId === 'string' && entry.sessionId) ||
    (typeof entry.session_id === 'string' && entry.session_id) ||
    'session-default';

  return {
    id: String(id),
    role: role === 'user' || role === 'assistant' ? role : 'system',
    content,
    createdAt,
    sessionId,
    personaId: entry.personaId || entry.persona_id || entry.agent_id || 'alden',
  };
};

const createLocalMessage = (content: string, sessionId: string, personaId: string): NormalizedChatMessage => ({
  id: `local-${Date.now()}-${Math.random().toString(36).slice(2)}`,
  role: 'user',
  content,
  createdAt: new Date().toISOString(),
  sessionId,
  personaId,
});

const normalizeSessionEvent = (entry: Partial<SessionEvent>): SessionEvent => {
  const timestamp = entry.timestamp ?? new Date().toISOString();
  const agentId = entry.agent_id ?? entry.persona_id ?? 'system';
  return {
    event_id: String(entry.event_id ?? `${agentId}-${timestamp}-${Math.random().toString(36).slice(2)}`),
    event: entry.event ?? 'system',
    agent_id: agentId,
    content: entry.content,
    timestamp,
    persona_id: entry.persona_id ?? agentId,
    visibility: entry.visibility ?? 'live',
    metadata: entry.metadata,
  };
};

const messageToSessionEvent = (
  message: NormalizedChatMessage,
  visibility: SessionEvent['visibility'] = 'live'
): SessionEvent =>
  normalizeSessionEvent({
    event_id: message.id,
    event: message.role === 'user' ? 'message' : 'response',
    agent_id: message.personaId ?? 'alden',
    content: message.content,
    timestamp: message.createdAt,
    persona_id: message.personaId ?? 'alden',
    visibility,
  });

const sessionEventToMessage = (event: SessionEvent, sessionId: string): NormalizedChatMessage | null => {
  if (!event.content) return null;
  return {
    id: event.event_id,
    role: event.event === 'message' ? 'user' : 'assistant',
    content: event.content,
    createdAt: event.timestamp,
    sessionId,
    personaId: event.persona_id ?? event.agent_id,
  };
};

const mergeMessages = (
  primary: NormalizedChatMessage[],
  secondary: NormalizedChatMessage[]
): NormalizedChatMessage[] => {
  const map = new Map<string, NormalizedChatMessage>();
  [...secondary, ...primary].forEach((msg) => {
    map.set(msg.id, msg);
  });
  return Array.from(map.values()).sort(
    (a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime()
  );
};

export const ExpandedChat: React.FC<ExpandedChatProps> = ({
  userId = 'default_user',
  roomId,
  topic,
  participants,
  sessionLog,
  breakouts,
  liveFeedSettings,
  onParticipantChange,
  onBreakoutCreate,
  onLogExport,
  onSessionEnd,
  auditLog,
  onClose,
  avatarStream,
}) => {
  const [message, setMessage] = useState('');
  const [isMicActive, setIsMicActive] = useState(false);
  const [isSoundEnabled, setIsSoundEnabled] = useState(true);
  const [conversationHistory, setConversationHistory] = useState<NormalizedChatMessage[]>([]);
  const [sessionLogEntries, setSessionLogEntries] = useState<SessionEvent[]>(() =>
    (sessionLog || []).map(normalizeSessionEvent)
  );
  const [participantState, setParticipantState] = useState<Participant[]>(() =>
    ensureAldenParticipant(participants)
  );
  const [isSending, setIsSending] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [isHistoryLoading, setIsHistoryLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState<string | null>(null);
  const [bridgeReady, setBridgeReady] = useState(false);
  const [personas, setPersonas] = useState<MimicPersona[]>([]);
  const [activePersonaId, setActivePersonaId] = useState<string>('alden');
  const [isLoadingPersonas, setIsLoadingPersonas] = useState(false);
  const [personaStatuses, setPersonaStatuses] = useState<Record<string, PersonaStatus>>(() => {
    const initial = ensureAldenParticipant(participants);
    return initial.reduce((acc, participant) => {
      if (participant.type === 'persona') {
        acc[participant.id] = participant.status ?? 'idle';
      }
      return acc;
    }, {} as Record<string, PersonaStatus>);
  });
  const personaStatusTimers = useRef<Record<string, ReturnType<typeof setTimeout>>>({});

  const aldenBridgeRef = useRef<AldenBridge | null>(null);
  const mimicBridgeRef = useRef<MimicBridge | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const resolvedSessionId = useMemo(
    () => roomId || `session-${userId || 'default_user'}`,
    [roomId, userId]
  );

  const hiddenResponses = useMemo(
    () => new Set(liveFeedSettings?.hidden_responses ?? []),
    [liveFeedSettings]
  );

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  const appendToSessionLog = useCallback((entries: SessionEvent | SessionEvent[]) => {
    const incoming = Array.isArray(entries) ? entries : [entries];
    setSessionLogEntries((prev) => {
      const map = new Map<string, SessionEvent>();
      prev.forEach((entry) => map.set(entry.event_id, entry));
      incoming.map(normalizeSessionEvent).forEach((entry) => {
        map.set(entry.event_id, { ...map.get(entry.event_id), ...entry });
      });
      return Array.from(map.values()).sort(
        (a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime()
      );
    });
  }, []);

  const updateParticipantStatus = useCallback(
    (personaId: string, status: PersonaStatus, resetAfterMs?: number) => {
      setPersonaStatuses((prev) => ({ ...prev, [personaId]: status }));
      setParticipantState((prev) => {
        const next = prev.map((participant) =>
          participant.id === personaId ? { ...participant, status } : participant
        );
        onParticipantChange(next);
        return next;
      });

      if (personaStatusTimers.current[personaId]) {
        clearTimeout(personaStatusTimers.current[personaId]);
      }
      if (resetAfterMs) {
        personaStatusTimers.current[personaId] = setTimeout(() => {
          setPersonaStatuses((prev) => ({ ...prev, [personaId]: 'idle' }));
          setParticipantState((prev) => {
            const next = prev.map((participant) =>
              participant.id === personaId ? { ...participant, status: 'idle' } : participant
            );
            onParticipantChange(next);
            return next;
          });
        }, resetAfterMs);
      }
    },
    [onParticipantChange]
  );

  const getPersonaLabel = useCallback(
    (personaId?: string) => {
      if (!personaId || personaId === 'alden') return 'Alden';
      const persona = personas.find((p) => p.persona_id === personaId);
      const participant = participantState.find((p) => p.id === personaId);
      return persona?.name || participant?.displayName || personaId;
    },
    [participantState, personas]
  );

  const getPersonaStatus = (personaId: string): PersonaStatus => {
    return personaStatuses[personaId] ?? 'idle';
  };

  useEffect(() => {
    scrollToBottom();
  }, [scrollToBottom, conversationHistory, isTyping, sessionLogEntries]);

  useEffect(() => {
    const nextParticipants = ensureAldenParticipant(participants);
    setParticipantState((prev) => {
      const next = nextParticipants.map((participant) => {
        const previous = prev.find((p) => p.id === participant.id);
        return { ...participant, status: previous?.status ?? participant.status ?? 'idle' };
      });
      onParticipantChange(next);
      return next;
    });
  }, [participants, onParticipantChange]);

  useEffect(() => {
    appendToSessionLog(sessionLog.map(normalizeSessionEvent));
  }, [appendToSessionLog, sessionLog]);

  useEffect(() => {
    const loadPersonas = async () => {
      setIsLoadingPersonas(true);
      try {
        const mimicBridge = new MimicBridge({ userId });
        mimicBridgeRef.current = mimicBridge;
        await mimicBridge.initialize();
        const response = await mimicBridge.getPersonas();
        if (response.success && response.data) {
          setPersonas(response.data);
          setParticipantState((prev) => {
            const existingIds = new Set(prev.map((p) => p.id));
            const additions = response.data
              .filter((persona) => !existingIds.has(persona.persona_id))
              .map<Participant>((persona) => ({
                id: persona.persona_id,
                type: 'persona',
                displayName: persona.name,
                role: 'Mimic',
                status: 'idle',
                avatarUrl: '/assets/Mimic.png',
              }));
            if (!additions.length) return prev;
            const next = [...prev, ...additions];
            onParticipantChange(next);
            return next;
          });
        }
      } catch (err) {
        console.error('Failed to load personas', err);
      } finally {
        setIsLoadingPersonas(false);
      }
    };
    loadPersonas();
  }, [userId, onParticipantChange]);

  useEffect(() => {
    if (!avatarStream || !avatarStream.length) return;
    avatarStream.forEach((stream) => {
      if (stream.isSpeaking) {
        updateParticipantStatus(stream.personaId, 'speaking', 4500);
      } else if (stream.isThinking) {
        updateParticipantStatus(stream.personaId, 'thinking', 6000);
      } else {
        updateParticipantStatus(stream.personaId, 'idle');
      }
    });
  }, [avatarStream, updateParticipantStatus]);

  const loadHistory = useCallback(async () => {
    if (!aldenBridgeRef.current) return;

    setIsHistoryLoading(true);
    setErrorMessage(null);

    try {
      const response = await aldenBridgeRef.current.getChatHistory(userId);
      if (response.success) {
        const normalized = (response.data || []).map((entry: any) => normalizeChatMessage(entry));
        setConversationHistory(normalized);
        appendToSessionLog(normalized.map((msg) => messageToSessionEvent(msg)));
      } else {
        setErrorMessage(response.error);
      }
    } catch (error: unknown) {
      setErrorMessage(error instanceof Error ? error.message : 'Failed to load chat history');
    } finally {
      setIsHistoryLoading(false);
    }
  }, [appendToSessionLog, userId]);

  useEffect(() => {
    let mounted = true;
    const bridge = new AldenBridge({ userId });
    aldenBridgeRef.current = bridge;
    setBridgeReady(false);
    setIsHistoryLoading(true);
    setErrorMessage(null);

    const init = async () => {
      try {
        await bridge.initialize();
        if (!mounted) return;
        setBridgeReady(true);
        await loadHistory();
      } catch (error: unknown) {
        if (mounted) {
          setErrorMessage(error instanceof Error ? error.message : 'Failed to initialize Alden chat');
        }
      } finally {
        if (mounted) {
          setIsHistoryLoading(false);
        }
      }
    };

    init();

    return () => {
      mounted = false;
      Object.values(personaStatusTimers.current).forEach((timer) => clearTimeout(timer));
      bridge.destroy();
      aldenBridgeRef.current = null;
      setBridgeReady(false);
    };
  }, [userId, loadHistory]);

  const handleSendMessage = async () => {
    const trimmed = message.trim();
    if (!trimmed || !aldenBridgeRef.current || isSending) {
      return;
    }

    setMessage('');
    setErrorMessage(null);
    setIsSending(true);
    setIsTyping(true);

    const localMessage = createLocalMessage(trimmed, resolvedSessionId, activePersonaId);
    setConversationHistory((prev) => [...prev, localMessage]);
    appendToSessionLog(messageToSessionEvent(localMessage));
    updateParticipantStatus(activePersonaId, 'thinking', 6000);

    try {
      let messageToSend = trimmed;
      if (activePersonaId !== 'alden') {
        messageToSend = `[Persona:${activePersonaId}] ${trimmed}`;
      }

      const response = await aldenBridgeRef.current.sendChatMessage(messageToSend);
      if (response.success && response.data) {
        const normalized = normalizeChatMessage(response.data);
        setConversationHistory((prev) => [...prev, normalized]);
        appendToSessionLog(messageToSessionEvent(normalized));
        updateParticipantStatus(normalized.personaId ?? 'alden', 'speaking', 4500);
      } else if (!response.success) {
        setErrorMessage(response.error);
      }
    } catch (error: unknown) {
      setErrorMessage(error instanceof Error ? error.message : 'Failed to send message');
    } finally {
      setIsSending(false);
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const getActivePersonaName = () => getPersonaLabel(activePersonaId);

  const personaCards = useMemo(() => {
    const mimicCards = personas.map((persona) => ({
      persona_id: persona.persona_id,
      name: persona.name,
      subtitle: persona.expertise_areas?.[0] || 'Specialist',
      avatar: '/assets/Mimic.png',
    }));

    const participantCards = participantState
      .filter((p) => p.type === 'persona' && !mimicCards.some((card) => card.persona_id === p.id) && p.id !== 'alden')
      .map((p) => ({
        persona_id: p.id,
        name: p.displayName || p.id,
        subtitle: p.role || 'Persona',
        avatar: p.avatarUrl || '/assets/Mimic.png',
      }));

    return [
      {
        persona_id: 'alden',
        name: 'Alden',
        subtitle: 'System Core',
        avatar: '/assets/Alden.png',
      },
      ...mimicCards,
      ...participantCards,
    ];
  }, [personas, participantState]);

  const liveFeedMessages = useMemo(() => {
    const logMessages = sessionLogEntries
      .filter((entry) => entry.visibility !== 'log-only')
      .map((entry) => sessionEventToMessage(entry, resolvedSessionId))
      .filter((entry): entry is NormalizedChatMessage => Boolean(entry));

    return mergeMessages(conversationHistory, logMessages).filter((msg) => !hiddenResponses.has(msg.id));
  }, [conversationHistory, hiddenResponses, resolvedSessionId, sessionLogEntries]);

  const handlePersonaSwitch = (personaId: string) => {
    setActivePersonaId(personaId);
    updateParticipantStatus(personaId, 'thinking', 2000);
  };

  const handlePersonaKeyNav = (index: number, direction: 'next' | 'prev') => {
    const nextIndex =
      direction === 'next'
        ? Math.min(personaCards.length - 1, index + 1)
        : Math.max(0, index - 1);
    const nextPersona = personaCards[nextIndex];
    if (nextPersona) {
      handlePersonaSwitch(nextPersona.persona_id);
    }
  };

  const handleBreakoutCreate = () => {
    const breakout: BreakoutRoom = {
      breakout_id: `${roomId}-breakout-${Date.now()}`,
      topic: `${topic || 'Live Session'} // ${getActivePersonaName()}`,
      participants: [activePersonaId],
      log: [],
    };
    onBreakoutCreate(breakout);
  };

  return (
    <div className="flex h-full w-full bg-[#020617] text-[#c5d2e8] font-sans overflow-hidden">
      {/* Sidebar - Persona Selector */}
      <div className="w-72 border-r border-[#d6b369]/30 flex flex-col bg-[#06101f]/95 backdrop-blur-md">
        <div className="p-4 border-b border-[#d6b369]/20 flex items-center gap-2">
          <Users className="text-[#d6b369]" size={20} />
          <h2 className="text-[#d6b369] font-orbitron-force uppercase tracking-widest text-sm font-bold">Personas</h2>
        </div>

        <div
          className="flex-1 overflow-y-auto p-2 space-y-2 scrollbar-thin scrollbar-thumb-[#d6b369]/20"
          role="listbox"
          aria-label="Persona selector"
        >
          {personaCards.map((persona, index) => {
            const isActive = activePersonaId === persona.persona_id;
            const status = getPersonaStatus(persona.persona_id);
            return (
              <button
                key={persona.persona_id}
                onClick={() => handlePersonaSwitch(persona.persona_id)}
                onKeyDown={(e) => {
                  if (e.key === 'ArrowDown') {
                    e.preventDefault();
                    handlePersonaKeyNav(index, 'next');
                  }
                  if (e.key === 'ArrowUp') {
                    e.preventDefault();
                    handlePersonaKeyNav(index, 'prev');
                  }
                }}
                role="option"
                aria-selected={isActive}
                className={`w-full p-3 rounded-lg flex items-center gap-3 transition-all border focus:outline-none focus:ring-2 focus:ring-[#00e6ff]/50 ${
                  isActive
                    ? 'bg-[#d6b369]/10 border-[#d6b369] shadow-[0_0_20px_rgba(214,179,105,0.18)]'
                    : 'border-transparent hover:bg-white/5 hover:border-white/10'
                }`}
              >
                <div
                  className={`relative w-12 h-12 rounded-full flex items-center justify-center border overflow-hidden ${
                    isActive ? 'border-[#d6b369]' : 'border-[#8fa7c7]/30'
                  }`}
                >
                  <img src={persona.avatar} alt={persona.name} className="w-full h-full object-cover opacity-90" />
                  <span
                    className={`absolute -right-1 -bottom-1 px-2 py-0.5 rounded-full text-[10px] font-semibold uppercase tracking-wide ${
                      status === 'speaking'
                        ? 'bg-[#d6b369] text-black'
                        : status === 'thinking'
                        ? 'bg-[#00e6ff] text-[#031324]'
                        : 'bg-[#1e293b] text-[#e2e8f0]'
                    }`}
                  >
                    {status}
                  </span>
                </div>
                <div className="text-left">
                  <div className={`font-bold text-sm ${isActive ? 'text-[#d6b369]' : 'text-[#e5f7ff]'}`}>
                    {persona.name}
                  </div>
                  <div className="text-[10px] text-[#8fa7c7]/80 uppercase tracking-wider truncate max-w-[150px]">
                    {persona.subtitle}
                  </div>
                </div>
              </button>
            );
          })}
          {isLoadingPersonas && (
            <div className="text-xs text-[#8fa7c7] px-3 py-2 rounded bg-[#0b1628]/70 border border-[#00e6ff]/20">
              Syncing personas…
            </div>
          )}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col relative">
        {/* Header */}
        <div className="h-18 border-b border-[#d6b369]/30 flex items-center justify-between px-6 bg-[#06101f]/90 backdrop-blur-md z-10">
          <div className="flex items-center gap-4">
            <div className="relative w-12 h-12 rounded-full border border-[#d6b369]/60 flex items-center justify-center bg-[#d6b369]/5 shadow-[0_0_25px_rgba(214,179,105,0.25)]">
              <img
                src={activePersonaId === 'alden' ? '/assets/Alden.png' : '/assets/Mimic.png'}
                alt="Avatar"
                className="w-9 h-9 object-contain"
              />
              <div
                className={`absolute -right-1 -bottom-1 w-3 h-3 rounded-full ${
                  getPersonaStatus(activePersonaId) === 'speaking'
                    ? 'bg-[#d6b369]'
                    : getPersonaStatus(activePersonaId) === 'thinking'
                    ? 'bg-[#00e6ff]'
                    : 'bg-[#334155]'
                } shadow-[0_0_10px_rgba(0,230,255,0.6)]`}
                aria-label={`Persona status ${getPersonaStatus(activePersonaId)}`}
              />
            </div>
            <div>
              <h2 className="text-lg font-orbitron-force font-bold text-[#d6b369] tracking-wider">
                {getActivePersonaName()}
              </h2>
              <div className="flex items-center gap-2 text-xs text-[#8fa7c7]">
                <div className="w-2 h-2 rounded-full bg-[#00e6ff] animate-pulse shadow-[0_0_8px_#00e6ff]" />
                <span className="uppercase tracking-[0.24em]">Live Feed • {topic}</span>
              </div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <button
              onClick={() => setIsSoundEnabled((prev) => !prev)}
              className={`p-2 text-[#8fa7c7] hover:text-[#d6b369] transition-colors rounded-lg hover:bg-white/5 border border-transparent ${
                isSoundEnabled ? 'bg-[#0b1628]/50' : 'bg-transparent'
              }`}
              aria-pressed={isSoundEnabled}
            >
              {isSoundEnabled ? <Volume2 size={20} /> : <VolumeX size={20} />}
            </button>
            <button
              onClick={handleBreakoutCreate}
              className="p-2 text-[#e5f7ff] border border-[#00e6ff]/40 rounded-lg hover:border-[#00e6ff] hover:bg-[#00e6ff]/10 transition flex items-center gap-2"
            >
              <Sparkles size={18} className="text-[#00e6ff]" />
              <span className="text-xs uppercase tracking-widest">Breakout</span>
            </button>
            <button
              onClick={onLogExport}
              className="p-2 text-[#e5f7ff] border border-[#d6b369]/50 rounded-lg hover:bg-[#d6b369]/10 hover:shadow-[0_0_12px_rgba(214,179,105,0.25)] transition flex items-center gap-2"
            >
              <Download size={18} className="text-[#d6b369]" />
              <span className="text-xs uppercase tracking-widest">Export Log</span>
            </button>
            {onClose && (
              <button
                onClick={onClose}
                className="p-2 text-[#8fa7c7] hover:text-[#d6b369] transition-colors rounded-lg hover:bg-white/5 flex items-center gap-2 border border-transparent"
              >
                <span className="text-xs uppercase tracking-widest font-bold">Minimize</span>
                <ChevronLeft size={20} className="rotate-180" />
              </button>
            )}
            <button
              onClick={onSessionEnd}
              className="p-2 text-[#fca5a5] border border-[#fca5a5]/40 rounded-lg hover:bg-[#fca5a5]/10 transition flex items-center gap-2"
            >
              <LogOut size={18} />
              <span className="text-xs uppercase tracking-widest">End</span>
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 grid grid-cols-[2fr_1fr] gap-4 p-6 overflow-hidden">
          {/* Live Feed */}
          <div className="relative bg-[#0b1628]/70 border border-[#d6b369]/25 rounded-2xl shadow-[0_10px_50px_rgba(0,0,0,0.35)] backdrop-blur-sm flex flex-col overflow-hidden">
            <div className="flex items-center justify-between px-5 py-3 border-b border-[#d6b369]/20">
              <div className="flex items-center gap-2">
                <MessageSquare size={18} className="text-[#00e6ff]" />
                <span className="text-xs uppercase tracking-[0.24em] text-[#8fa7c7]">Live Feed</span>
              </div>
              <div className="flex items-center gap-2 text-[11px] text-[#8fa7c7]">
                <span className="px-2 py-0.5 rounded bg-[#041225] border border-[#00e6ff]/20">
                  Verbosity: {liveFeedSettings?.verbosity || 'default'}
                </span>
                {isHistoryLoading && <span className="text-[#d6b369]">Syncing…</span>}
              </div>
            </div>

            <div className="p-5 pb-3">
              <div className="relative w-full rounded-xl border border-[#00e6ff]/20 bg-gradient-to-br from-[#041225] to-[#0b1628] px-4 py-3 flex items-center justify-between">
                <div>
                  <p className="text-[10px] uppercase tracking-[0.26em] text-[#8fa7c7] mb-1">Avatar Channel</p>
                  <p className="text-sm text-[#e5f7ff]">Visualizer ready for {getActivePersonaName()}</p>
                </div>
                <div className="relative w-20 h-20 rounded-full border-2 border-[#00e6ff]/50 flex items-center justify-center bg-[#071020] shadow-[0_0_20px_rgba(0,230,255,0.25)]">
                  <div className="w-14 h-14 rounded-full border border-[#d6b369]/60 flex items-center justify-center">
                    <div className="w-9 h-9 rounded-full bg-gradient-to-br from-[#00e6ff]/40 to-[#d6b369]/30 animate-pulse" />
                  </div>
                  <div className="absolute -right-1 -bottom-1 text-[10px] px-2 py-0.5 rounded-full bg-[#d6b369]/90 text-black uppercase font-semibold tracking-wide">
                    {getPersonaStatus(activePersonaId)}
                  </div>
                </div>
              </div>
            </div>

            <div className="flex-1 overflow-y-auto px-5 pb-4 space-y-4 relative">
              <div className="absolute inset-0 bg-[url('/assets/grid.png')] opacity-[0.03] pointer-events-none" />
              {liveFeedMessages.map((entry) => (
                <div
                  key={entry.id}
                  className={`flex ${entry.role === 'user' ? 'justify-end' : 'justify-start'} relative z-10`}
                >
                  <div
                    className={`max-w-[78%] rounded-xl p-4 border backdrop-blur-sm ${
                      entry.role === 'user'
                        ? 'bg-[#0b1124]/90 border-[#00e6ff]/30 text-[#e5f7ff] rounded-tr-none shadow-[0_0_18px_rgba(0,230,255,0.08)]'
                        : 'bg-[#0a0f1d]/95 border-[#d6b369]/30 text-[#c5d2e8] rounded-tl-none shadow-[0_0_18px_rgba(214,179,105,0.08)]'
                    }`}
                  >
                    <div className="flex items-center gap-2 mb-2 opacity-80">
                      <span
                        className={`text-[10px] uppercase tracking-widest font-bold ${
                          entry.role === 'user' ? 'text-[#00e6ff]' : 'text-[#d6b369]'
                        }`}
                      >
                        {entry.role === 'user' ? 'You' : getPersonaLabel(entry.personaId)}
                      </span>
                      <span className="text-[10px] text-[#8fa7c7]">
                        {new Date(entry.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </span>
                    </div>
                    <p className="text-sm leading-relaxed whitespace-pre-wrap">{entry.content}</p>
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="flex justify-start relative z-10">
                  <div className="bg-[#0a0f1d]/90 border border-[#d6b369]/30 rounded-xl rounded-tl-none p-4 flex items-center gap-2">
                    <div className="w-2 h-2 bg-[#d6b369] rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                    <div className="w-2 h-2 bg-[#d6b369] rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                    <div className="w-2 h-2 bg-[#d6b369] rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                  </div>
                </div>
              )}
              <div ref={messagesEndRef} />
            </div>
          </div>

          {/* Session Log */}
          <div className="bg-[#0b1628]/70 border border-[#00e6ff]/25 rounded-2xl shadow-[0_10px_40px_rgba(0,0,0,0.35)] backdrop-blur-sm overflow-hidden">
            <div className="flex items-center justify-between px-4 py-3 border-b border-[#00e6ff]/25">
              <div className="flex items-center gap-2">
                <MessageSquare size={16} className="text-[#d6b369]" />
                <span className="text-xs uppercase tracking-[0.24em] text-[#8fa7c7]">Session Log</span>
              </div>
              <span className="text-[11px] text-[#8fa7c7]">Audit trail • {auditLog?.length ?? 0} entries</span>
            </div>
            <div className="max-h-full overflow-y-auto p-4 space-y-3 scrollbar-thin scrollbar-thumb-[#00e6ff]/20">
              {sessionLogEntries.map((event) => (
                <div
                  key={event.event_id}
                  className="p-3 rounded-lg border border-[#00e6ff]/20 bg-[#031324]/70"
                >
                  <div className="flex items-center justify-between text-[11px] text-[#8fa7c7] mb-1">
                    <span className="flex items-center gap-2">
                      <span
                        className={`px-2 py-0.5 rounded-full uppercase font-semibold tracking-wide ${
                          event.event === 'response'
                            ? 'bg-[#d6b369]/20 text-[#d6b369]'
                            : event.event === 'message'
                            ? 'bg-[#00e6ff]/20 text-[#00e6ff]'
                            : 'bg-[#1f2937] text-[#e5e7eb]'
                        }`}
                      >
                        {event.event}
                      </span>
                      <span>{event.agent_id}</span>
                    </span>
                    <span>{new Date(event.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</span>
                  </div>
                  {event.content && <p className="text-sm text-[#e5f7ff] leading-relaxed">{event.content}</p>}
                </div>
              ))}
              {!sessionLogEntries.length && (
                <div className="text-xs text-[#8fa7c7] p-3 rounded border border-dashed border-[#00e6ff]/30">
                  No log entries yet. Messages and events will appear here even if hidden from the live feed.
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Input Area */}
        <div className="p-6 bg-[#06101f]/95 border-t border-[#d6b369]/30 backdrop-blur-md z-20">
          <div className="relative flex items-end gap-4 max-w-5xl mx-auto">
            <div className="flex-1 relative group">
              <div className="absolute -inset-0.5 bg-gradient-to-r from-[#d6b369]/50 to-[#00e6ff]/50 rounded-lg opacity-30 group-hover:opacity-70 transition-opacity blur-[2px]" />
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={`Message ${getActivePersonaName()}...`}
                className="relative w-full bg-[#020617] border border-[#d6b369]/30 rounded-lg px-4 py-3 text-[#e5f7ff] placeholder-[#8fa7c7]/50 focus:outline-none focus:border-[#00e6ff]/70 focus:ring-1 focus:ring-[#00e6ff]/30 transition-all resize-none min-h-[60px]"
                rows={1}
              />
            </div>

            <button
              onClick={() => setIsMicActive(!isMicActive)}
              className={`p-4 rounded-lg border transition-all ${
                isMicActive
                  ? 'bg-[#00e6ff]/20 border-[#00e6ff] text-[#00e6ff] shadow-[0_0_15px_rgba(0,230,255,0.3)]'
                  : 'bg-[#020617] border-[#d6b369]/30 text-[#8fa7c7] hover:text-[#d6b369] hover:border-[#d6b369]/70'
              }`}
              aria-pressed={isMicActive}
            >
              {isMicActive ? <Mic size={20} /> : <MicOff size={20} />}
            </button>

            <button
              onClick={handleSendMessage}
              disabled={!message.trim() || isSending}
              className="p-4 rounded-lg bg-[#d6b369]/10 border border-[#d6b369]/50 text-[#d6b369] hover:bg-[#d6b369]/20 hover:shadow-[0_0_15px_rgba(214,179,105,0.2)] transition-all disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <Send size={20} />
            </button>
          </div>
          <div className="text-center mt-3">
            <span className="text-[10px] uppercase tracking-[0.2em] text-[#8fa7c7]/60">
              Alden Cognitive Systems // v4.2.0
            </span>
          </div>
          {errorMessage && (
            <div className="text-center text-sm text-[#fca5a5] mt-2 bg-[#2a0e0e]/70 border border-[#fca5a5]/40 rounded-lg py-2">
              {errorMessage}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
