+  const [activePersonaId, setActivePersonaId] = useState<string>('alden');
+  const [isLoadingPersonas, setIsLoadingPersonas] = useState(false);
+  const [personaStatuses, setPersonaStatuses] = useState<Record<string, PersonaStatus>>(() => {
