    let key_manager = Arc::new(key_manager::SecureKeyManager::new());
    let authenticator = Arc::new(auth::SecureAuthenticator::new(key_manager.clone()));

    // Initialize secure vault with security components
    let secure_vault = secure_vault::SecureVault::new(key_manager.clone(), authenticator.clone());

    let key_manager_for_setup = key_manager.clone();
    let authenticator_for_setup = authenticator.clone();

    // Note: Using cfg attributes directly with .invoke_handler() for better type inference
    #[cfg(debug_assertions)]
    let invoke_handler: Box<dyn Fn(tauri::Invoke) + Send + Sync> =
        Box::new(include!("invoke_handler_debug.inc"));

    #[cfg(not(debug_assertions))]
    let invoke_handler: Box<dyn Fn(tauri::Invoke) + Send + Sync> =
        Box::new(include!("invoke_handler_release.inc"));

    tauri::Builder::default()
        .manage(secure_vault)
        .setup(move |app| {
            let window = app.get_window("main").unwrap();
            let resource_dir = app
                .path_resolver()
                .resource_dir()
                .expect("Failed to get resource directory");
            println!("Resource directory: {}", resource_dir.display());

            // Initialize Settings Manager
            let settings_manager = settings::SettingsManager::new(&app.handle());
            let settings_state = settings::SettingsState {
                manager: std::sync::Mutex::new(settings_manager),
            };
            app.manage(settings_state);

            // Initialize database-backed managers before commands can run
            let key_manager = key_manager_for_setup.clone();
            let authenticator = authenticator_for_setup.clone();
            let app_handle = app.handle();
            let handle_for_init = app_handle.clone();
            tauri::async_runtime::block_on(async move {
                match db_manager::DatabaseManager::initialize(&handle_for_init).await {
                    Ok(db) => {
                        use tokio::sync::RwLock;
                        let db_arc = Arc::new(RwLock::new(Some(db)));

                        // Initialize Task Manager
                        let task_manager = alden::tasks::TaskManager::new(db_arc.clone());
                        if let Err(e) = task_manager.initialize_schema().await {
                            eprintln!("[Alden] Failed to initialize task schema: {}", e);
                        }
                        let alden_state = AldenState {
                            task_manager: Arc::new(RwLock::new(task_manager)),
                        };
                        handle_for_init.manage(alden_state);

                        // Initialize Calendar Manager
                        let calendar_manager =
                            alden::calendar::CalendarManager::new(db_arc.clone());
                        if let Err(e) = calendar_manager.initialize_schema().await {
                            eprintln!("[Alden] Failed to initialize calendar schema: {}", e);
