                        let chat_manager = alden::chat::ChatManager::new();
                        let chat_state = ChatState {
                            chat_manager: Arc::new(chat_manager),
                            history: Arc::new(RwLock::new(HashMap::new())),
                            typing_status: Arc::new(RwLock::new(HashMap::new())),
