// Secure Key Management Module
// Handles encryption key storage using OS-level credential storage

use base64::{engine::general_purpose, Engine as _};
use keyring::Entry;
use rand::{rngs::OsRng, RngCore};
use std::sync::Arc;
use tokio::sync::RwLock;

const KEYRING_SERVICE: &str = "hearthlink";
const VAULT_KEY_NAME: &str = "vault_master_key";
const JWT_SECRET_NAME: &str = "jwt_secret";

#[cfg(debug_assertions)]
macro_rules! debug_log {
    ($($arg:tt)*) => {
        println!($($arg)*);
    };
}

#[cfg(not(debug_assertions))]
macro_rules! debug_log {
    ($($arg:tt)*) => {};
}

pub struct SecureKeyManager {
    vault_key_cache: Arc<RwLock<Option<Vec<u8>>>>,
    jwt_secret_cache: Arc<RwLock<Option<Vec<u8>>>>,
}

#[allow(dead_code)]
impl SecureKeyManager {
    pub fn new() -> Self {
        Self {
            vault_key_cache: Arc::new(RwLock::new(None)),
            jwt_secret_cache: Arc::new(RwLock::new(None)),
        }
    }

    /// Get or create the vault master encryption key
    /// Uses OS-level secure storage (Keychain on macOS, Credential Manager on Windows, Secret Service on Linux)
    pub async fn get_vault_key(&self) -> Result<Vec<u8>, String> {
        // Check cache first
        {
            let cache = self.vault_key_cache.read().await;
            if let Some(key) = cache.as_ref() {
                debug_log!("[KEY_MANAGER] Using cached vault key");
                return Ok(key.clone());
            }
        }

        debug_log!("[KEY_MANAGER] Loading vault key from OS credential storage");

        // Try to load from OS keyring
        let keyring = Entry::new(KEYRING_SERVICE, VAULT_KEY_NAME)
            .map_err(|e| format!("Failed to access keyring: {}", e))?;

        let key = match keyring.get_password() {
            Ok(key_b64) => {
                debug_log!("[KEY_MANAGER] Found existing vault key in keyring");
                general_purpose::STANDARD
                    .decode(key_b64)
                    .map_err(|e| format!("Invalid key format in keyring: {}", e))?
            }
            Err(_) => {
                debug_log!("[KEY_MANAGER] No existing key found, generating new vault key");

                // Generate new 256-bit key
                let mut key = vec![0u8; 32];
                OsRng.fill_bytes(&mut key);

                // Store in OS keyring
                let key_b64 = general_purpose::STANDARD.encode(&key);
                keyring
                    .set_password(&key_b64)
                    .map_err(|e| format!("Failed to store key in keyring: {}", e))?;

                debug_log!("[KEY_MANAGER] New vault key generated and stored securely");
                key
            }
        };

        // Validate key length
        if key.len() != 32 {
            return Err(format!(
                "Invalid key length: expected 32 bytes, got {}",
                key.len()
            ));
        }

        // Cache the key
        {
            let mut cache = self.vault_key_cache.write().await;
            *cache = Some(key.clone());
        }

        Ok(key)
    }

    /// Get or create the JWT signing secret
    /// Uses OS-level secure storage
    pub async fn get_jwt_secret(&self) -> Result<Vec<u8>, String> {
        // Check cache first
        {
            let cache = self.jwt_secret_cache.read().await;
            if let Some(secret) = cache.as_ref() {
                debug_log!("[KEY_MANAGER] Using cached JWT secret");
                return Ok(secret.clone());
            }
        }

        debug_log!("[KEY_MANAGER] Loading JWT secret from OS credential storage");

        // Try to load from OS keyring
        let keyring = Entry::new(KEYRING_SERVICE, JWT_SECRET_NAME)
            .map_err(|e| format!("Failed to access keyring: {}", e))?;

        let secret = match keyring.get_password() {
            Ok(secret_b64) => {
                debug_log!("[KEY_MANAGER] Found existing JWT secret in keyring");
                general_purpose::STANDARD
                    .decode(secret_b64)
                    .map_err(|e| format!("Invalid secret format in keyring: {}", e))?
            }
            Err(_) => {
                debug_log!("[KEY_MANAGER] No existing secret found, generating new JWT secret");

                // Generate new 256-bit secret
                let mut secret = vec![0u8; 32];
                OsRng.fill_bytes(&mut secret);

                // Store in OS keyring
                let secret_b64 = general_purpose::STANDARD.encode(&secret);
                keyring
                    .set_password(&secret_b64)
                    .map_err(|e| format!("Failed to store secret in keyring: {}", e))?;

                debug_log!("[KEY_MANAGER] New JWT secret generated and stored securely");
                secret
            }
        };

        // Validate secret length
        if secret.len() != 32 {
            return Err(format!(
                "Invalid secret length: expected 32 bytes, got {}",
                secret.len()
            ));
        }

        // Cache the secret
        {
            let mut cache = self.jwt_secret_cache.write().await;
            *cache = Some(secret.clone());
        }

        Ok(secret)
    }

    /// Rotate the vault encryption key
    /// WARNING: This will require re-encrypting all existing data
    pub async fn rotate_vault_key(&self) -> Result<Vec<u8>, String> {
        debug_log!("[KEY_MANAGER] Rotating vault encryption key");

        // Generate new key
        let mut new_key = vec![0u8; 32];
        OsRng.fill_bytes(&mut new_key);

        // Store in OS keyring
        let keyring = Entry::new(KEYRING_SERVICE, VAULT_KEY_NAME)
            .map_err(|e| format!("Failed to access keyring: {}", e))?;

        let key_b64 = general_purpose::STANDARD.encode(&new_key);
        keyring
            .set_password(&key_b64)
            .map_err(|e| format!("Failed to store new key in keyring: {}", e))?;

        // Update cache
        {
            let mut cache = self.vault_key_cache.write().await;
            *cache = Some(new_key.clone());
        }

        debug_log!("[KEY_MANAGER] Vault key rotated successfully");
        Ok(new_key)
    }

    /// Rotate the JWT signing secret
    /// WARNING: This will invalidate all existing tokens
    pub async fn rotate_jwt_secret(&self) -> Result<Vec<u8>, String> {
        debug_log!("[KEY_MANAGER] Rotating JWT signing secret");

        // Generate new secret
        let mut new_secret = vec![0u8; 32];
        OsRng.fill_bytes(&mut new_secret);

        // Store in OS keyring
        let keyring = Entry::new(KEYRING_SERVICE, JWT_SECRET_NAME)
            .map_err(|e| format!("Failed to access keyring: {}", e))?;

        let secret_b64 = general_purpose::STANDARD.encode(&new_secret);
        keyring
            .set_password(&secret_b64)
            .map_err(|e| format!("Failed to store new secret in keyring: {}", e))?;

        // Update cache
        {
            let mut cache = self.jwt_secret_cache.write().await;
            *cache = Some(new_secret.clone());
        }

        debug_log!("[KEY_MANAGER] JWT secret rotated successfully");
        Ok(new_secret)
    }

    /// Clear cached keys (for testing or security purposes)
    pub async fn clear_cache(&self) {
        let mut vault_cache = self.vault_key_cache.write().await;
        *vault_cache = None;

        let mut jwt_cache = self.jwt_secret_cache.write().await;
        *jwt_cache = None;

        debug_log!("[KEY_MANAGER] Key cache cleared");
    }

    /// Delete all keys from OS credential storage
    /// WARNING: This is destructive and will make encrypted data unrecoverable
    #[allow(dead_code)]
    pub async fn delete_all_keys(&self) -> Result<(), String> {
        debug_log!("[KEY_MANAGER] WARNING: Deleting all keys from credential storage");

        // Delete vault key
        let vault_keyring = Entry::new(KEYRING_SERVICE, VAULT_KEY_NAME)
            .map_err(|e| format!("Failed to access vault keyring: {}", e))?;

        if let Err(e) = vault_keyring.delete_password() {
            debug_log!("[KEY_MANAGER] Warning: Failed to delete vault key: {}", e);
        }

        // Delete JWT secret
        let jwt_keyring = Entry::new(KEYRING_SERVICE, JWT_SECRET_NAME)
            .map_err(|e| format!("Failed to access JWT keyring: {}", e))?;

        if let Err(e) = jwt_keyring.delete_password() {
            debug_log!("[KEY_MANAGER] Warning: Failed to delete JWT secret: {}", e);
        }

        // Clear cache
        self.clear_cache().await;

        debug_log!("[KEY_MANAGER] All keys deleted");
        Ok(())
    }

    /// Store a versioned key in the OS keyring
    pub async fn store_versioned_key(&self, key_name: &str, key: &[u8]) -> Result<(), String> {
        let keyring = Entry::new(KEYRING_SERVICE, key_name)
            .map_err(|e| format!("Failed to access keyring for {}: {}", key_name, e))?;
        let encoded = general_purpose::STANDARD.encode(key);
        keyring
            .set_password(&encoded)
            .map_err(|e| format!("Failed to store {} in keyring: {}", key_name, e))?;
        Ok(())
    }

    /// Retrieve a versioned key from the OS keyring
    pub async fn get_versioned_key(&self, key_name: &str) -> Result<Option<Vec<u8>>, String> {
        let keyring = Entry::new(KEYRING_SERVICE, key_name)
            .map_err(|e| format!("Failed to access keyring for {}: {}", key_name, e))?;

        match keyring.get_password() {
            Ok(payload) => general_purpose::STANDARD
                .decode(payload)
                .map(Some)
                .map_err(|e| format!("Invalid key format for {}: {}", key_name, e)),
            Err(keyring::Error::NoEntry) => Ok(None),
            Err(e) => Err(format!("Failed to read {} from keyring: {}", key_name, e)),
        }
    }

    /// Remove a versioned key from the OS keyring
    pub async fn delete_versioned_key(&self, key_name: &str) -> Result<(), String> {
        let keyring = Entry::new(KEYRING_SERVICE, key_name)
            .map_err(|e| format!("Failed to access keyring for {}: {}", key_name, e))?;

        match keyring.delete_password() {
            Ok(_) => Ok(()),
            Err(keyring::Error::NoEntry) => Ok(()),
            Err(e) => Err(format!("Failed to delete {} from keyring: {}", key_name, e)),
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_key_generation() {
        let km = SecureKeyManager::new();

        // Clean up any existing keys first for test isolation
        let _ = km.delete_all_keys().await;
        km.clear_cache().await;

        // Get vault key (should generate new one)
        let key1 = km.get_vault_key().await.unwrap();
        assert_eq!(key1.len(), 32);

        // Get again (should return cached)
        let key2 = km.get_vault_key().await.unwrap();
        assert_eq!(key1, key2);

        // Clear cache
        km.clear_cache().await;

        // Get again (should load from keyring, same key)
        // Note: In test environments, keyring behavior may vary
        // This test primarily verifies key generation and caching
        let key3 = km.get_vault_key().await.unwrap();
        assert_eq!(key1, key3);

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }

    #[tokio::test]
    async fn test_key_rotation() {
        let km = SecureKeyManager::new();

        let key1 = km.get_vault_key().await.unwrap();
        let key2 = km.rotate_vault_key().await.unwrap();

        assert_ne!(key1, key2);
        assert_eq!(key2.len(), 32);

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }

    #[tokio::test]
    async fn test_jwt_secret() {
        let km = SecureKeyManager::new();

        let secret1 = km.get_jwt_secret().await.unwrap();
        assert_eq!(secret1.len(), 32);

        let secret2 = km.get_jwt_secret().await.unwrap();
        assert_eq!(secret1, secret2);

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }
}

