mod tests {
    use super::*;

    #[tokio::test]
    async fn test_token_generation_and_verification() {
        let km = Arc::new(SecureKeyManager::new());
        let auth = SecureAuthenticator::new(km.clone());

        // Generate token
        let token = auth.generate_token("test_user", 1).await.unwrap();
        assert!(!token.is_empty());

        // Verify token
        let user_id = auth.verify_token(&token).await.unwrap();
        assert_eq!(user_id, "test_user");

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }

    #[tokio::test]
    async fn test_token_revocation() {
        let km = Arc::new(SecureKeyManager::new());
        let auth = SecureAuthenticator::new(km.clone());

        let token = auth.generate_token("test_user", 1).await.unwrap();

        // Token should be valid
        assert!(auth.verify_token(&token).await.is_ok());

        // Revoke token
        auth.revoke_token(&token).await.unwrap();

        // Token should now be invalid
        assert!(auth.verify_token(&token).await.is_err());

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }

    #[tokio::test]
    async fn test_user_tokens_revocation() {
        let km = Arc::new(SecureKeyManager::new());
        let auth = SecureAuthenticator::new(km.clone());

        // Generate multiple tokens for same user
        let token1 = auth.generate_token("test_user", 1).await.unwrap();
        let token2 = auth.generate_token("test_user", 1).await.unwrap();
        let token3 = auth.generate_token("other_user", 1).await.unwrap();

        // All should be valid
        assert!(auth.verify_token(&token1).await.is_ok());
        assert!(auth.verify_token(&token2).await.is_ok());
        assert!(auth.verify_token(&token3).await.is_ok());

        // Revoke all tokens for test_user
        let count = auth.revoke_user_tokens("test_user").await.unwrap();
        assert_eq!(count, 2);

        // test_user tokens should be invalid
        assert!(auth.verify_token(&token1).await.is_err());
        assert!(auth.verify_token(&token2).await.is_err());

        // other_user token should still be valid
        assert!(auth.verify_token(&token3).await.is_ok());

        // Cleanup
        km.delete_all_keys().await.unwrap();
    }

    #[tokio::test]
    async fn test_constant_time_comparison() {
        let km = Arc::new(SecureKeyManager::new());
        let auth = SecureAuthenticator::new(km);

        // Test that user ID matching uses constant-time comparison
        assert!(auth.verify_user_match("user1", "user1"));
        assert!(!auth.verify_user_match("user1", "user2"));
        assert!(!auth.verify_user_match("user1", "user11")); // Different length
    }
