pub struct MemorySliceManager {
    db_manager: Arc<RwLock<Option<DatabaseManager>>>,
    key_manager: Arc<SecureKeyManager>,
}

impl MemorySliceManager {
    pub fn new(
        db_manager: Arc<RwLock<Option<DatabaseManager>>>,
        key_manager: Arc<SecureKeyManager>,
    ) -> Self {
        Self {
            db_manager,
            key_manager,
        }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db_guard = self.db_manager.read().await;
        let db = db_guard.as_ref().ok_or("Database not initialized")?;

        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS memory_slices (
                record_id TEXT PRIMARY KEY,
                memory_id TEXT NOT NULL,
                user_id TEXT NOT NULL,
                persona_id TEXT NOT NULL,
                version INTEGER NOT NULL,
                memory_type TEXT NOT NULL,
                encrypted_content TEXT NOT NULL,
                nonce TEXT NOT NULL,
                tags TEXT,
                importance REAL NOT NULL DEFAULT 0.5,
                status TEXT NOT NULL DEFAULT 'active',
                created_at TEXT NOT NULL,
                updated_at TEXT NOT NULL,
                UNIQUE(memory_id, version),
                CHECK(status IN ('active','archived','deleted')),
                CHECK(memory_type IN ('episodic','semantic','procedural','working','conversation','task','knowledge','context','other'))
            )"#,
        )
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create memory_slices table: {}", e))?;

        sqlx::query("CREATE INDEX IF NOT EXISTS idx_memory_user_persona ON memory_slices(user_id, persona_id)")
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to create user/persona index: {}", e))?;

        sqlx::query("CREATE INDEX IF NOT EXISTS idx_memory_type ON memory_slices(memory_type)")
            .execute(&db.pool)
            .await
            .map_err(|e| format!("Failed to create memory_type index: {}", e))?;

        println!("[VAULT] Memory slices schema initialized (encrypted storage)");
        Ok(())
    }

    async fn encrypt_content(&self, plaintext: &str) -> Result<(String, String), String> {
        let key = self.key_manager.get_vault_key().await?;
        let key_array = GenericArray::from_slice(&key);
        let cipher = Aes256Gcm::new(key_array);
        let mut nonce_bytes = [0u8; 12];
        OsRng.fill_bytes(&mut nonce_bytes);
        let nonce = Nonce::from_slice(&nonce_bytes);
        let ciphertext = cipher
            .encrypt(&nonce, plaintext.as_bytes())
            .map_err(|e| format!("Encryption failed: {}", e))?;
        Ok((
            general_purpose::STANDARD.encode(&ciphertext),
            general_purpose::STANDARD.encode(&nonce_bytes),
        ))
    }

    async fn decrypt_content(
        &self,
        ciphertext_b64: &str,
        nonce_b64: &str,
    ) -> Result<String, String> {
        let key = self.key_manager.get_vault_key().await?;
        let key_array = GenericArray::from_slice(&key);
        let cipher = Aes256Gcm::new(key_array);

        let nonce_bytes = general_purpose::STANDARD
            .decode(nonce_b64)
            .map_err(|e| format!("Invalid nonce: {}", e))?;
        let nonce = Nonce::from_slice(&nonce_bytes);

        let ciphertext = general_purpose::STANDARD
            .decode(ciphertext_b64)
            .map_err(|e| format!("Invalid ciphertext: {}", e))?;

        let plaintext_bytes = cipher
            .decrypt(&nonce, ciphertext.as_ref())
            .map_err(|e| format!("Decryption failed: {}", e))?;

        String::from_utf8(plaintext_bytes).map_err(|e| format!("Invalid UTF-8: {}", e))
    }

    fn tags_to_json(tags: Option<Vec<String>>) -> Option<String> {
        tags.and_then(|list| serde_json::to_string(&list).ok())
    }

    fn json_to_tags(json: Option<String>) -> Vec<String> {
        json.and_then(|s| serde_json::from_str::<Vec<String>>(&s).ok())
            .unwrap_or_default()
    }

    fn clamp_importance(value: Option<f64>) -> f64 {
        let mut importance = value.unwrap_or(0.5);
        if importance.is_nan() {
            importance = 0.5;
        }
        if importance < 0.0 {
            importance = 0.0;
        }
        if importance > 1.0 {
            importance = 1.0;
        }
        importance
    }

    fn to_memory(record: MemoryRecord) -> Memory {
        Memory {
            id: record.memory_id,
            user_id: record.user_id,
            persona_id: record.persona_id,
            memory_type: record.memory_type,
            content: record.encrypted_content,
            tags: Self::json_to_tags(record.tags),
            importance: record.importance,
            created_at: record.created_at,
            updated_at: record.updated_at,
            version: record.version,
        }
    }

    async fn latest_version(
