use serde::{Deserialize, Serialize};
use std::io::Write;
use std::process::{Command, Stdio};

/// Legacy Alden Persona Python IPC Bridge
///
/// OPTIONAL: Provides subprocess communication with Python-based Alden persona system.
/// This is OPTIONAL and will gracefully fail if Python is not installed.
/// Modern Rust implementation is available in the alden/ module and is preferred.
///
/// Note: This will be fully removed once remaining functionality is migrated to Rust.

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AldenGenerateRequest {
    pub user_id: String,
    pub message: String,
    pub session_id: Option<String>,
    pub context: Option<serde_json::Value>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AldenGenerateResponse {
    pub response: String,
    pub session_id: Option<String>,
    pub metadata: Option<serde_json::Value>,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AldenStatusResponse {
    pub persona_name: String,
    pub relationship_level: u8,
    pub total_interactions: u64,
    pub trust_score: f64,
    pub last_interaction: Option<String>,
    pub current_traits: serde_json::Value,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AldenUpdateTraitRequest {
    pub user_id: String,
    pub trait_name: String,
    pub new_value: i32,
    pub reason: Option<String>,
}

/// Call Python IPC wrapper via subprocess
/// Returns graceful error if Python is not installed
fn call_python_ipc(
    app_handle: &tauri::AppHandle,
    request: serde_json::Value,
) -> Result<serde_json::Value, String> {
    // Get Python executable
    let python_exe = if cfg!(windows) { "python" } else { "python3" };

    // Get script path from bundled resources or development path
    let ipc_script = app_handle
        .path_resolver()
        .resolve_resource("src/cli/alden_ipc.py")
        .unwrap_or_else(|| std::path::PathBuf::from("src/cli/alden_ipc.py"));

    // Try to spawn Python process - gracefully fail if Python not available
    let mut child = match Command::new(python_exe)
        .arg(ipc_script.to_string_lossy().to_string())
        .stdin(Stdio::piped())
        .stdout(Stdio::piped())
        .stderr(Stdio::piped())
        .spawn()
    {
        Ok(child) => child,
        Err(e) => {
            eprintln!("[alden_legacy] Python not available: {}", e);
            return Err(format!(
                "Legacy Alden features require Python 3.8+. \
                Please install Python or use the modern Rust implementation (Alden tasks, calendar, email). \
                Error: {}", e
            ));
        }
    };

    // Write JSON request to stdin
    if let Some(mut stdin) = child.stdin.take() {
        let json_str = serde_json::to_string(&request)
            .map_err(|e| format!("Failed to serialize request: {}", e))?;

        stdin
            .write_all(json_str.as_bytes())
            .map_err(|e| format!("Failed to write to Python stdin: {}", e))?;
    }

    // Wait for process to complete and capture output
    let output = child
        .wait_with_output()
        .map_err(|e| format!("Failed to read Python output: {}", e))?;

    if !output.status.success() {
        let stderr = String::from_utf8_lossy(&output.stderr);
        return Err(format!("Python process failed: {}", stderr));
    }

    // Parse JSON response
    let stdout = String::from_utf8_lossy(&output.stdout);
    let result: serde_json::Value = serde_json::from_str(&stdout)
        .map_err(|e| format!("Failed to parse Python response: {}", e))?;

    Ok(result)
}

/// Generate response from Alden persona (Python backend)
#[tauri::command]
pub async fn alden_generate(
    app_handle: tauri::AppHandle,
    user_id: String,
    message: String,
    session_id: Option<String>,
    context: Option<serde_json::Value>,
) -> Result<AldenGenerateResponse, String> {
    // Build request JSON
    let request = serde_json::json!({
        "command": "generate",
        "user_id": user_id,
        "message": message,
        "session_id": session_id,
        "context": context
    });

    // Call Python via subprocess
    let response = call_python_ipc(&app_handle, request)?;

    // Parse response
    let result: AldenGenerateResponse = serde_json::from_value(response)
        .map_err(|e| format!("Failed to parse Alden response: {}", e))?;

    Ok(result)
}

/// Get Alden's current status and metrics (Python backend)
#[tauri::command]
pub async fn alden_get_status(
    app_handle: tauri::AppHandle,
    user_id: String,
) -> Result<AldenStatusResponse, String> {
    let request = serde_json::json!({
        "command": "status",
        "user_id": user_id
    });

    let response = call_python_ipc(&app_handle, request)?;

    let result: AldenStatusResponse = serde_json::from_value(response)
        .map_err(|e| format!("Failed to parse Alden status: {}", e))?;

    Ok(result)
}

/// Update Alden's trait (Python backend)
#[tauri::command]
pub async fn alden_update_trait(
    app_handle: tauri::AppHandle,
    user_id: String,
    trait_name: String,
    new_value: i32,
    reason: Option<String>,
) -> Result<serde_json::Value, String> {
    let request = serde_json::json!({
        "command": "update_trait",
        "user_id": user_id,
        "trait_name": trait_name,
        "new_value": new_value,
        "reason": reason
    });

    let response = call_python_ipc(&app_handle, request)?;
    Ok(response)
}

/// Export Alden's memory for user review (Python backend)
#[tauri::command]
pub async fn alden_export_memory(
    app_handle: tauri::AppHandle,
    user_id: String,
) -> Result<serde_json::Value, String> {
    let request = serde_json::json!({
        "command": "export_memory",
        "user_id": user_id
    });

    let response = call_python_ipc(&app_handle, request)?;
    Ok(response)
}

