  async getAvailability(
    startDate: string,
    endDate: string,
    userId: string = this.userId,
    durationMinutes: number = 30
  ): Promise<BridgeResponse<AvailabilitySlot[]>> {
    const parseDateToUnix = (value: string, label: string): number => {
      const timestamp = Date.parse(value);
      if (Number.isNaN(timestamp)) {
        throw new Error(`Invalid ${label} provided`);
      }
      return Math.floor(timestamp / 1000);
    };

    let startUnix: number;
    let endUnix: number;
    try {
      startUnix = parseDateToUnix(startDate, 'start date');
      endUnix = parseDateToUnix(endDate, 'end date');
    } catch (parseError: any) {
      return {
        success: false,
        error: parseError?.message || 'Invalid date provided',
        code: 'CALENDAR_AVAILABILITY_INVALID_DATE',
      };
    }

    try {
      const request = {
        user_id: userId,
        start_time: startUnix,
        end_time: endUnix,
        duration_minutes: durationMinutes,
      };

      const availability = await this.invokeCommand<AvailabilitySlot[]>('alden_calendar_availability_get', { request });
      return { success: true, data: availability };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || 'Failed to fetch availability',
        code: 'CALENDAR_AVAILABILITY_ERROR',
      };
    }
  }

