    /// Handle incoming chat message and generate response via Ollama
    pub async fn handle_message(&self, request: HandleMessageRequest) -> Result<String, String> {
        let message = request.message.trim();

        if message.is_empty() {
            return Err("Message cannot be empty".to_string());
        }

        // Try LLM first, fallback to keyword matching if Ollama unavailable
        match self.call_ollama(message).await {
            Ok(response) => Ok(response),
            Err(e) => {
                eprintln!(
                    "[Alden Chat] Ollama error: {}. Falling back to keyword matching.",
                    e
                );
                Ok(self.generate_fallback_response(message))
            }
        }
    }

    /// Call Ollama API for LLM response
    async fn call_ollama(&self, user_message: &str) -> Result<String, String> {
        let client = reqwest::Client::builder()
            .timeout(std::time::Duration::from_secs(30))
            .build()
            .map_err(|e| format!("Failed to create HTTP client: {}", e))?;

        let request_body = OllamaChatRequest {
            model: self.ollama_model.clone(),
            messages: vec![
                OllamaMessage {
                    role: "system".to_string(),
                    content: self.get_system_prompt(),
                },
                OllamaMessage {
                    role: "user".to_string(),
                    content: user_message.to_string(),
                },
            ],
            stream: false,
        };

        let url = format!("{}/api/chat", self.ollama_url);

        let response = client
            .post(&url)
            .json(&request_body)
            .send()
            .await
            .map_err(|e| {
                if e.is_connect() {
                    "Ollama is not running. Please start Ollama to use AI chat.".to_string()
                } else if e.is_timeout() {
                    "Ollama request timed out. The model might be too large or busy.".to_string()
                } else {
                    format!("Failed to connect to Ollama: {}", e)
                }
            })?;

        if !response.status().is_success() {
            let status = response.status();
            let error_text = response.text().await.unwrap_or_default();
            return Err(format!("Ollama API error ({}): {}", status, error_text));
        }

        let ollama_response: OllamaChatResponse = response
            .json()
            .await
            .map_err(|e| format!("Failed to parse Ollama response: {}", e))?;

        Ok(ollama_response.message.content)
    }

