// ============================================================================
// State Management
// ============================================================================

pub struct ChatState {
    pub chat_manager: Arc<ChatManager>,
    pub history: Arc<RwLock<HashMap<String, Vec<ChatMessageEntry>>>>,
    pub typing_status: Arc<RwLock<HashMap<String, bool>>>,
}

impl ChatState {
    async fn append_history(&self, user_id: &str, entry: ChatMessageEntry) {
        let mut history = self.history.write().await;
        let entries = history.entry(user_id.to_string()).or_default();
        entries.push(entry);
        if entries.len() > MAX_CHAT_HISTORY {
            let overflow = entries.len() - MAX_CHAT_HISTORY;
            entries.drain(0..overflow);
        }
    }

    async fn set_typing(&self, user_id: &str, is_typing: bool) {
        let mut typing = self.typing_status.write().await;
        typing.insert(user_id.to_string(), is_typing);
    }
}

// ============================================================================
// Tauri Commands
// ============================================================================

/// Handle a chat message from the user
#[tauri::command]
pub async fn alden_chat_handle_message(
    request: HandleMessageRequest,
    state: State<'_, ChatState>,
) -> Result<String, String> {
    state.chat_manager.handle_message(request).await
}

/// Send a chat message via AldenBridge
#[tauri::command]
pub async fn alden_chat_send(
    request: SendChatMessageRequest,
    state: State<'_, ChatState>,
) -> Result<ChatMessageEntry, String> {
    let trimmed = request.message.trim();
    if trimmed.is_empty() {
        return Err("Message cannot be empty".to_string());
    }

    let user_message =
        ChatMessageEntry::new(&request.user_id, "user", trimmed, &request.session_id);
    state.append_history(&request.user_id, user_message).await;

    let response_text = state
        .chat_manager
        .handle_message(HandleMessageRequest {
            user_id: request.user_id.clone(),
            message: trimmed.to_string(),
            session_id: request.session_id.clone(),
        })
        .await?;

    let response_entry = ChatMessageEntry::new(
        &request.user_id,
        "alden",
        &response_text,
        &request.session_id,
    );
    state
        .append_history(&request.user_id, response_entry.clone())
        .await;

    Ok(response_entry)
}

/// Retrieve chat history for a user
#[tauri::command]
pub async fn alden_chat_history(
    request: ChatHistoryRequest,
    state: State<'_, ChatState>,
) -> Result<Vec<ChatMessageEntry>, String> {
    let history = state.history.read().await;
    let entries = history.get(&request.user_id).cloned().unwrap_or_default();
    if let Some(limit) = request.limit {
        let len = entries.len();
        let start = len.saturating_sub(limit);
        Ok(entries[start..].to_vec())
    } else {
        Ok(entries)
    }
}

/// Clear chat history for a user
#[tauri::command]
pub async fn alden_chat_clear(
    request: ClearChatRequest,
    state: State<'_, ChatState>,
) -> Result<(), String> {
    let mut history = state.history.write().await;
    history.remove(&request.user_id);
    Ok(())
}

/// Set typing indicator state for a user
#[tauri::command]
pub async fn alden_chat_set_typing_indicator(
    request: TypingIndicatorRequest,
    state: State<'_, ChatState>,
) -> Result<(), String> {
    state.set_typing(&request.user_id, request.is_typing).await;
    Ok(())
