+/// Collect a richer system metrics payload for dashboard widgets.
+#[tauri::command]
+pub fn get_system_metrics() -> Result<SystemMetrics, String> {
+    let mut sys = System::new_with_specifics(
+        RefreshKind::new()
+            .with_cpu(CpuRefreshKind::everything())
+            .with_memory()
+            .with_disks_list()
+            .with_disks(),
+    );
+
+    sys.refresh_cpu();
+    sys.refresh_memory();
+    sys.refresh_disks_list();
+    sys.refresh_disks();
+
+    let cpu_load = sys.global_cpu_info().cpu_usage();
+
+    let total_memory_kb = sys.total_memory();
+    let used_memory_kb = sys.used_memory();
+    let memory_total_mb = total_memory_kb / 1024;
+    let memory_used_mb = used_memory_kb / 1024;
+    let memory_used_percent = if total_memory_kb > 0 {
+        (used_memory_kb as f32 / total_memory_kb as f32) * 100.0
+    } else {
+        0.0
+    };
+
+    let mut disk_total_kb = 0;
+    let mut disk_used_kb = 0;
+    for disk in sys.disks() {
+        let total = disk.total_space();
+        let available = disk.available_space();
+        disk_total_kb += total / 1024;
+        disk_used_kb += (total - available) / 1024;
+    }
+
+    let disk_total_mb = disk_total_kb / 1024;
+    let disk_used_mb = disk_used_kb / 1024;
+    let disk_used_percent = if disk_total_kb > 0 {
+        (disk_used_kb as f32 / disk_total_kb as f32) * 100.0
+    } else {
+        0.0
+    };
+
+    Ok(SystemMetrics {
+        cpu_load,
+        memory_used_mb,
+        memory_total_mb,
+        memory_used_percent,
+        disk_used_mb,
+        disk_total_mb,
+        disk_used_percent,
+        process_count: sys.processes().len(),
+        uptime_seconds: sys.uptime(),
+        timestamp: Utc::now().to_rfc3339(),
+        source: "native".to_string(),
