export interface AldenCommandCenterProps {
  userId: string;
  onBackToLauncher?: () => void;
  onNavigateToModule?: (moduleName: string) => void;
}

type ViewMode = 'overview' | 'tasks' | 'calendar' | 'email' | 'chat';
type DashboardPreset = 'standard' | 'mission' | 'schedule' | 'compact';
type DashboardTileId = 'counter-tasks' | 'counter-schedule' | 'counter-inbox' | 'counter-uptime' | 'panel-mission' | 'panel-schedule';

const FRAME_PADDING = 16;
const TILE_GAP = 12;
const MIN_Y_BUFFER = 100; // Increased from 48 to clear header/frame top
const ROW_HEIGHT = 160;   // Adjusted for counter height (140) + buffer
const ROW_GAP = 60;       // Increased from 40 for better separation
const ROW0_Y = FRAME_PADDING + MIN_Y_BUFFER;
const ROW1_Y = ROW0_Y + ROW_HEIGHT + ROW_GAP;

interface DashboardLayoutItem {
  id: DashboardTileId;
  x: number;
  y: number;
  width: number;
  height: number;
  locked?: boolean;
}

const DEFAULT_DASHBOARD_LAYOUT: DashboardLayoutItem[] = [
  { id: 'counter-tasks', x: FRAME_PADDING, y: ROW0_Y, width: 240, height: 140, locked: true },
  { id: 'counter-schedule', x: 240 + FRAME_PADDING * 2, y: ROW0_Y, width: 240, height: 140, locked: true },
  { id: 'counter-inbox', x: 240 * 2 + FRAME_PADDING * 3, y: ROW0_Y, width: 240, height: 140, locked: true },
  { id: 'counter-uptime', x: 240 * 3 + FRAME_PADDING * 4, y: ROW0_Y, width: 240, height: 140, locked: true },
  { id: 'panel-mission', x: FRAME_PADDING, y: ROW1_Y, width: 600, height: 240, locked: true },
  { id: 'panel-schedule', x: 600 + FRAME_PADDING * 2 + TILE_GAP, y: ROW1_Y, width: 280, height: 240, locked: true }
];

interface MetricCardProps {
  title: string;
  value: number;
  subtitle: string;
  accentColor: 'cyan' | 'gold' | 'green';
  icon: React.ReactNode;
  active?: boolean;
  variant?: 'counter';
}

const MetricCard: React.FC<MetricCardProps> = ({ title, value, subtitle, accentColor, icon, active, variant = 'counter' }) => {
  const styles = {
    cyan: 'text-[#9fb4d6]',
    gold: 'text-alden-gold',
    green: 'text-alden-cyan',
  } as const;

  const activeStyle: React.CSSProperties = {
    border: '1px solid rgba(0, 240, 255, 0.55)',
    boxShadow: '0 8px 20px rgba(0, 240, 255, 0.18), inset 0 0 0 1px rgba(197,160,89,0.38)',
    background: 'linear-gradient(135deg, rgba(0,240,255,0.12), rgba(0,240,255,0.03) 42%, rgba(197,160,89,0.06))',
  };

  const inactiveStyle: React.CSSProperties = {
    border: '1px solid rgba(120, 135, 170, 0.55)',
    boxShadow: '0 6px 14px rgba(0, 0, 0, 0.35)',
    background: 'linear-gradient(135deg, rgba(255,255,255,0.03), rgba(0,0,0,0.18) 45%, rgba(0,0,0,0.35))',
  };

  const valueColor = active
    ? 'text-[#e5f7ff]'
    : accentColor === 'gold'
      ? 'text-alden-gold'
      : accentColor === 'green'
        ? 'text-[#72f0ff]'
        : styles.cyan;

  const borderClasses = active
    ? 'border ring-1 ring-[rgba(0,240,255,0.32)] bg-gradient-to-br from-[rgba(0,240,255,0.08)] via-transparent to-[rgba(0,0,0,0.25)]'
    : 'border ring-1 ring-[rgba(255,255,255,0.12)] hover:border-[rgba(0,240,255,0.35)]';

  const variantSizing = {
    counter: 'basis-[230px] w-[230px] h-[130px] min-h-[130px]'
  } as const;

  const tabs: UnifiedTabItem[] = [
    { id: 'overview', label: 'Overview' },
    { id: 'tasks', label: 'Tasks' },
    { id: 'calendar', label: 'Calendar' },
    { id: 'email', label: 'Email' },
  ];
  const tabSelection = tabs.find(tab => tab.id === activeView) ? activeView : tabs[0].id;

