                );
                meta
            }),
            priority: FeedPriority::Low,
        });
    }

    // Synapse context entries
    if context_filter.is_none() || matches!(context_filter, Some(FeedContext::Synapse)) {
        entries.push(PrivateFeedEntry {
            id: format!("synapse-{}", Uuid::new_v4()),
            user_id: user_id.to_string(),
            context: FeedContext::Synapse,
            content: "Plugin connection established successfully".to_string(),
            timestamp: Utc::now().to_rfc3339(),
            visibility: FeedVisibility::Private,
            metadata: Some({
                let mut meta = HashMap::new();
                meta.insert(
                    "category".to_string(),
                    serde_json::Value::String("connectivity".to_string()),
                );
                meta.insert(
                    "plugin_id".to_string(),
                    serde_json::Value::String("mcp-server-1".to_string()),
                );
                meta
            }),
            priority: FeedPriority::Medium,
        });

        entries.push(PrivateFeedEntry {
            id: format!("synapse-{}", Uuid::new_v4()),
            user_id: user_id.to_string(),
            context: FeedContext::Synapse,
            content: "Security scan completed - no threats detected".to_string(),
            timestamp: Utc::now().to_rfc3339(),
            visibility: FeedVisibility::Private,
            metadata: Some({
                let mut meta = HashMap::new();
                meta.insert(
                    "category".to_string(),
                    serde_json::Value::String("security".to_string()),
                );
                meta.insert(
                    "threats_found".to_string(),
                    serde_json::Value::Number(serde_json::Number::from(0)),
                );
                meta
            }),
            priority: FeedPriority::Low,
        });
    }

    // Sort by timestamp (newest first) and apply limit
    entries.sort_by(|a, b| b.timestamp.cmp(&a.timestamp));
    entries.truncate(limit as usize);

    entries
