/// Collect a richer system metrics payload for dashboard widgets.
#[tauri::command]
pub fn get_system_metrics() -> Result<SystemMetrics, String> {
    let mut sys = System::new_with_specifics(
        RefreshKind::new()
            .with_cpu(CpuRefreshKind::everything())
            .with_memory(MemoryRefreshKind::everything()),
    );

    // Wait a bit for CPU usage calculation
    std::thread::sleep(std::time::Duration::from_millis(200));
    sys.refresh_cpu();

    let cpu_load = sys.global_cpu_info().cpu_usage();

    // sysinfo 0.30 returns bytes
    let total_memory = sys.total_memory();
    let used_memory = sys.used_memory();
    let memory_total_mb = total_memory / 1024 / 1024;
    let memory_used_mb = used_memory / 1024 / 1024;
    let memory_used_percent = if total_memory > 0 {
        (used_memory as f32 / total_memory as f32) * 100.0
    } else {
        0.0
    };

    let disks = Disks::new_with_refreshed_list();
    let mut disk_total_bytes = 0;
    let mut disk_used_bytes = 0;
    for disk in &disks {
        let total = disk.total_space();
        let available = disk.available_space();
        disk_total_bytes += total;
        disk_used_bytes += total - available;
    }

    let disk_total_mb = disk_total_bytes / 1024 / 1024;
    let disk_used_mb = disk_used_bytes / 1024 / 1024;
    let disk_used_percent = if disk_total_bytes > 0 {
        (disk_used_bytes as f32 / disk_total_bytes as f32) * 100.0
    } else {
        0.0
    };

    Ok(SystemMetrics {
        cpu_load,
        memory_used_mb,
        memory_total_mb,
        memory_used_percent,
        disk_used_mb,
        disk_total_mb,
        disk_used_percent,
        process_count: 0, // Skipping processes for now to avoid overhead/complexity
        uptime_seconds: System::uptime(),
        timestamp: Utc::now().to_rfc3339(),
        source: "native".to_string(),
    })
}

