import { invoke } from '@tauri-apps/api/tauri';
import { BaseBridge, BridgeConfig } from './BaseBridge';
import { BridgeResponse, SystemProps, SystemTelemetry } from '../types/agents';

/**
 * System Bridge - lightweight IPC layer for runtime telemetry.
 *
 * Provides fast CPU/latency samples for the MythTech dashboard without
 * hitting heavyweight diagnostics pipelines.
 */
export class SystemBridge extends BaseBridge<SystemProps> {
  private telemetry: SystemTelemetry | null = null;
  private poller: number | null = null;
  private pollIntervalMs = 2000;

  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: 'system',
      ...config,
    });
  }

  protected async onInitialize(): Promise<void> {
    this.telemetry = null;
    this.poller = null;
  }

  protected async onDestroy(): Promise<void> {
    this.stopTelemetryStream();
    this.telemetry = null;
  }

  async getProps(): Promise<SystemProps> {
    if (!this.telemetry) {
      await this.refreshTelemetry();
    }

    // Start a background poller for near-real-time updates.
    if (!this.poller) {
      this.startTelemetryStream();
    }

    return {
      telemetry: this.telemetry,
      refreshTelemetry: this.refreshTelemetry.bind(this),
    };
  }

  /**
   * Fetch a single telemetry sample from the native backend.
   */
  async refreshTelemetry(): Promise<BridgeResponse<SystemTelemetry>> {
    try {
      const telemetry = await invoke<SystemTelemetry>('system_get_telemetry');
      this.telemetry = telemetry;
      this.notifySubscribers({ type: 'telemetry', data: telemetry });

      return { success: true, data: telemetry };
    } catch (error) {
      return this.createErrorResponse<SystemTelemetry>(
        error,
        'Failed to fetch system telemetry',
        'TELEMETRY_ERROR'
      );
    }
  }

  /**
   * Begin polling telemetry at the desired interval (default: 2s).
   */
  startTelemetryStream(intervalMs = 2000): void {
    if (this.poller || typeof window === 'undefined') return;
    this.pollIntervalMs = intervalMs;

    this.poller = window.setInterval(async () => {
      await this.refreshTelemetry();
    }, this.pollIntervalMs);
  }

  /**
   * Stop telemetry polling.
   */
  stopTelemetryStream(): void {
    if (this.poller && typeof window !== 'undefined') {
      window.clearInterval(this.poller);
      this.poller = null;
    }
  }
}

