pub struct AliceManager {
    db: Arc<RwLock<Option<DatabaseManager>>>,
}

impl AliceManager {
    pub fn new(db: Arc<RwLock<Option<DatabaseManager>>>) -> Self {
        Self { db }
    }

    pub async fn initialize_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let db = db.as_ref().ok_or("Database not initialized")?;

        // Create Alice tables
        let queries = vec![
            // Behavioral profiles table
            r#"
                CREATE TABLE IF NOT EXISTS alice_behavioral_profiles (
                    profile_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    communication_style TEXT NOT NULL,
                    attention_patterns TEXT NOT NULL, -- JSON
                    emotional_indicators TEXT NOT NULL, -- JSON
                    learning_preferences TEXT NOT NULL, -- JSON
                    created_at TEXT NOT NULL,
                    updated_at TEXT NOT NULL
                )
            "#,
            // Insights table
            r#"
                CREATE TABLE IF NOT EXISTS alice_insights (
                    insight_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    category TEXT NOT NULL,
                    title TEXT NOT NULL,
                    content TEXT NOT NULL,
                    confidence_score REAL NOT NULL,
                    created_at TEXT NOT NULL,
                    dismissed BOOLEAN DEFAULT FALSE
                )
            "#,
            // Analysis results table
            r#"
                CREATE TABLE IF NOT EXISTS alice_pattern_analysis (
                    analysis_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    analysis_type TEXT NOT NULL,
                    results TEXT NOT NULL, -- JSON
                    created_at TEXT NOT NULL
                )
            "#,
            // Behavioral signals table (from Python implementation)
            r#"
                CREATE TABLE IF NOT EXISTS alice_behavioral_signals (
                    signal_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    focus REAL NOT NULL,
                    stress REAL NOT NULL,
                    engagement REAL NOT NULL,
                    cognitive_load REAL NOT NULL,
                    tags TEXT NOT NULL, -- JSON array
                    evidence TEXT NOT NULL, -- JSON object
                    created_at TEXT NOT NULL
                )
            "#,
            // Consent decisions table
            r#"
                CREATE TABLE IF NOT EXISTS alice_consent_decisions (
                    consent_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    granted BOOLEAN NOT NULL,
                    scope TEXT NOT NULL, -- JSON array
                    reason TEXT NOT NULL,
                    actor TEXT NOT NULL,
                    created_at TEXT NOT NULL
                )
            "#,
            // Escalations table
            r#"
                CREATE TABLE IF NOT EXISTS alice_escalations (
                    escalation_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    target_persona TEXT NOT NULL,
                    risk_level TEXT NOT NULL,
                    metrics TEXT NOT NULL, -- JSON object
                    recommended_actions TEXT NOT NULL, -- JSON array
                    created_at TEXT NOT NULL,
                    resolved BOOLEAN DEFAULT FALSE
                )
            "#,
            // Deep Work Sessions table
            r#"
                CREATE TABLE IF NOT EXISTS alice_deep_work_sessions (
                    session_id TEXT PRIMARY KEY,
                    user_id TEXT NOT NULL,
                    start_time TEXT NOT NULL,
                    end_time TEXT,
                    duration_seconds INTEGER,
                    goal TEXT,
                    status TEXT NOT NULL, -- active, completed, aborted
                    created_at TEXT NOT NULL
                )
            "#,
        ];

        for query in queries {
            sqlx::query(query)
                .execute(&db.pool)
                .await
                .map_err(|e| format!("Failed to create Alice table: {}", e))?;
        }

        Ok(())
    }
}

