/// Generate daily check-in summary
#[tauri::command]
pub async fn alice_daily_checkin(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: DailyCheckinRequest,
) -> Result<String, String> {
    let signals = alice_get_signals(
        db,
        GetSignalsRequest {
            user_id: request.user_id,
            limit: Some(5),
        },
    )
    .await?;

    if signals.is_empty() {
        return Ok("No recent behavioral data available for check-in.".to_string());
    }

    let latest = &signals[0];
    let summary = format!(
        "Daily check-in: Focus {:.0}/100, Stress {:.0}/100, Engagement {:.0}/100, Cognitive Load {:.0}/100",
        latest.focus, latest.stress, latest.engagement, latest.cognitive_load
    );

    Ok(summary)
}

/// Prepare escalation to another persona (Alden, Mimic)
#[tauri::command]
pub async fn alice_prepare_escalation(
    db: State<'_, Arc<RwLock<Option<DatabaseManager>>>>,
    request: PrepareEscalationRequest,
) -> Result<Option<serde_json::Value>, String> {
    let signals = alice_get_signals(
        db.clone(),
        GetSignalsRequest {
            user_id: request.user_id.clone(),
            limit: Some(1),
        },
    )
    .await?;

    if signals.is_empty() {
        return Ok(None);
    }

    let latest = &signals[0];

    // Check if escalation is needed
    if latest.stress < 70.0 && latest.focus > 45.0 && latest.cognitive_load < 75.0 {
        return Ok(None);
    }

    let risk_level = if latest.stress >= 80.0 || latest.focus <= 35.0 {
        "high"
    } else {
        "medium"
    };

    let escalation_id = format!("escalation_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();

    let metrics = serde_json::json!({
        "focus": latest.focus,
        "stress": latest.stress,
        "engagement": latest.engagement,
        "cognitive_load": latest.cognitive_load,
    });

    let recommended_actions = vec![
        "Schedule guided decompression".to_string(),
        format!(
            "Review workload distribution with {}",
            request.target_persona
        ),
    ];

    let db_lock = db.read().await;
    let db = db_lock.as_ref().ok_or("Database not initialized")?;

    let query = r#"
        INSERT INTO alice_escalations (
            escalation_id, user_id, target_persona, risk_level, metrics, recommended_actions, created_at
        ) VALUES (?, ?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&escalation_id)
        .bind(&request.user_id)
        .bind(&request.target_persona)
        .bind(risk_level)
        .bind(&serde_json::to_string(&metrics).map_err(|e| e.to_string())?)
        .bind(&serde_json::to_string(&recommended_actions).map_err(|e| e.to_string())?)
        .bind(&created_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to create escalation: {}", e))?;

    Ok(Some(serde_json::json!({
        "escalation_id": escalation_id,
        "target": request.target_persona,
        "user_id": request.user_id,
        "risk_level": risk_level,
        "metrics": metrics,
        "recommended_actions": recommended_actions,
    })))
}

