fn validate_stop_status(status: &str) -> Result<(), String> {
    match status {
        "completed" | "aborted" => Ok(()),
        _ => Err("Invalid Deep Work status. Must be 'completed' or 'aborted'".to_string()),
    }
}

async fn alice_start_deep_work_internal(
    db: &DatabaseManager,
    request: &StartDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    // Check if there's already an active session
    let check_query = r#"
        SELECT session_id FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        LIMIT 1
    "#;

    let active_exists = sqlx::query(check_query)
        .bind(&request.user_id)
        .fetch_optional(&db.pool)
        .await
        .map_err(|e| format!("Failed to check active sessions: {}", e))?;

    if active_exists.is_some() {
        return Err("An active Deep Work session already exists".to_string());
    }

    let session_id = format!("deepwork_{}", chrono::Utc::now().timestamp_millis());
    let created_at = chrono::Utc::now().to_rfc3339();
    let start_time = created_at.clone();

    let query = r#"
        INSERT INTO alice_deep_work_sessions (
            session_id, user_id, start_time, goal, status, created_at
        ) VALUES (?, ?, ?, ?, ?, ?)
    "#;

    sqlx::query(query)
        .bind(&session_id)
        .bind(&request.user_id)
        .bind(&start_time)
        .bind(&request.goal)
        .bind("active")
        .bind(&created_at)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to start Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id,
        user_id: request.user_id.clone(),
        start_time,
        end_time: None,
        duration_seconds: None,
        goal: request.goal.clone(),
        status: "active".to_string(),
        created_at,
    })
}

async fn alice_stop_deep_work_internal(
    db: &DatabaseManager,
    request: &StopDeepWorkRequest,
) -> Result<AliceDeepWorkSession, String> {
    validate_stop_status(&request.status)?;

    // Get the active session
    let get_query = r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ? AND status = 'active'
        ORDER BY created_at DESC
        LIMIT 1
    "#;

    let session_row = sqlx::query_as::<_, AliceDeepWorkSession>(get_query)
        .bind(&request.user_id)
        .fetch_optional(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch active session: {}", e))?
        .ok_or("No active Deep Work session found")?;

    // Calculate duration
    let end_time = chrono::Utc::now();
    let start_time = chrono::DateTime::parse_from_rfc3339(&session_row.start_time)
        .map_err(|e| format!("Invalid start time format: {}", e))?
        .with_timezone(&chrono::Utc);

    let duration = end_time
        .signed_duration_since(start_time)
        .num_seconds()
        .max(0);
    let end_time_str = end_time.to_rfc3339();

    let update_query = r#"
        UPDATE alice_deep_work_sessions
        SET end_time = ?, duration_seconds = ?, status = ?
        WHERE session_id = ?
    "#;

    sqlx::query(update_query)
        .bind(&end_time_str)
        .bind(duration)
        .bind(&request.status)
        .bind(&session_row.session_id)
        .execute(&db.pool)
        .await
        .map_err(|e| format!("Failed to stop Deep Work session: {}", e))?;

    Ok(AliceDeepWorkSession {
        session_id: session_row.session_id,
        user_id: session_row.user_id,
        start_time: session_row.start_time,
        end_time: Some(end_time_str),
        duration_seconds: Some(duration),
        goal: session_row.goal,
        status: request.status.clone(),
        created_at: session_row.created_at,
    })
}

async fn alice_get_deep_work_history_internal(
    db: &DatabaseManager,
    request: &GetDeepWorkHistoryRequest,
) -> Result<Vec<AliceDeepWorkSession>, String> {
    let limit = request.limit.unwrap_or(20);

    let query = r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ?
        ORDER BY created_at DESC
        LIMIT ?
    "#;

    let sessions = sqlx::query_as::<_, AliceDeepWorkSession>(query)
        .bind(&request.user_id)
        .bind(limit)
        .fetch_all(&db.pool)
        .await
        .map_err(|e| format!("Failed to fetch Deep Work history: {}", e))?;

    Ok(sessions)
}

async fn alice_get_deep_work_analysis_internal(
    db: &DatabaseManager,
    user_id: &str,
) -> Result<DeepWorkAnalysis, String> {
    let sessions = sqlx::query_as::<_, AliceDeepWorkSession>(
        r#"
        SELECT session_id, user_id, start_time, end_time, duration_seconds, goal, status, created_at
        FROM alice_deep_work_sessions
        WHERE user_id = ?
        ORDER BY created_at DESC
        LIMIT 200
    "#,
    )
    .bind(user_id)
    .fetch_all(&db.pool)
    .await
    .map_err(|e| format!("Failed to fetch Deep Work sessions: {}", e))?;

    let total_sessions = sessions.len() as i64;
    let completed_sessions = sessions
        .iter()
        .filter(|s| s.status == "completed")
        .count() as i64;
    let aborted_sessions = sessions
        .iter()
        .filter(|s| s.status == "aborted")
        .count() as i64;
    let active_sessions = sessions
        .iter()
        .filter(|s| s.status == "active")
        .count() as i64;

    let durations: Vec<i64> = sessions
        .iter()
        .filter_map(|s| s.duration_seconds)
        .collect();
    let average_session_duration_seconds = if durations.is_empty() {
        0.0
    } else {
        durations.iter().sum::<i64>() as f64 / durations.len() as f64
    };

    let completion_rate = if total_sessions == 0 {
        0.0
    } else {
        completed_sessions as f64 / total_sessions as f64
    };

    #[derive(FromRow)]
    struct SignalAverages {
        focus_avg: Option<f64>,
        stress_avg: Option<f64>,
        engagement_avg: Option<f64>,
    }

    let thirty_days_ago = (chrono::Utc::now() - chrono::Duration::days(30)).to_rfc3339();

    let averages = sqlx::query_as::<_, SignalAverages>(
        r#"
        SELECT
            AVG(focus) as focus_avg,
            AVG(stress) as stress_avg,
            AVG(engagement) as engagement_avg
        FROM alice_behavioral_signals
        WHERE user_id = ? AND created_at >= ?
    "#,
    )
    .bind(user_id)
    .bind(thirty_days_ago)
    .fetch_one(&db.pool)
    .await
    .unwrap_or(SignalAverages {
        focus_avg: None,
        stress_avg: None,
        engagement_avg: None,
    });

    Ok(DeepWorkAnalysis {
        total_sessions,
        completed_sessions,
        aborted_sessions,
        active_sessions,
        average_session_duration_seconds,
        completion_rate,
        focus_score: averages.focus_avg.unwrap_or(0.0),
        stress_score: averages.stress_avg.unwrap_or(0.0),
        engagement_score: averages.engagement_avg.unwrap_or(0.0),
    })
}

