/**
 * Hearthlink Agent System Type Definitions
 *
 * This file contains all TypeScript type definitions for agent bridge system.
 * Based on: docs/hearthlink_system_documentation_master.md
 *
 * @version 1.3.0
 * @date 2025-10-09
 */

// ============================================================================
// Base Types
// ============================================================================

export type AgentId =
  | 'alden'
  | 'alice'
  | 'mimic'
  | 'vault'
  | 'core'
  | 'system'
  | 'synapse'
  | 'sentry'
  | 'user'
  | 'settings';

export interface BaseAgent {
  agentId: AgentId;
  status: 'active' | 'inactive' | 'error';
  lastActivity: string; // Changed to string
}

export type JsonValue = string | number | boolean | null | JsonObject | JsonArray;
export interface JsonObject {
  [key: string]: JsonValue;
}
export type JsonArray = JsonValue[];

export interface ApiResponse<T = unknown> {
  success: boolean;
  data?: T;
  error?: string;
  timestamp: string; // Changed to string
}

// ============================================================================
// Alden - Primary Local Agent/Persona
// ============================================================================

export interface AldenTraits {
  openness: number; // 0-100
  conscientiousness: number; // 0-100
  extraversion: number; // 0-100
  agreeableness: number; // 0-100
  emotional_stability: number; // 0-100
}

export interface CorrectionEvent {
  type: 'positive' | 'negative';
  timestamp: string; // Changed to string
  context?: string;
}

export interface SessionMood {
  session_id: string;
  mood: 'positive' | 'neutral' | 'negative';
  score: number; // 0-100
}

export interface RelationshipEvent {
  event: string;
  timestamp: string; // ISO 8601 string for Rust IPC compatibility
  delta: number;
}

export interface AuditEvent {
  action: 'create' | 'edit' | 'delete' | 'export' | 'reset';
  by: 'user' | 'system' | 'admin';
  timestamp: string; // Changed to string
  field?: string;
  oldValue?: JsonValue;
  newValue?: JsonValue;
}

export interface AldenPersonaMemory {
  persona_id: 'alden';
  user_id: string;
  schema_version: string;
  timestamp: string; // Changed to string
  traits: AldenTraits;
  motivation_style: string;
  trust_level: number; // 0-1
  feedback_score: number; // 0-100
  learning_agility: number;
  reflective_capacity: number;
  habit_consistency: number; // 0-1
  engagement: number;
  correction_events: CorrectionEvent[];
  session_mood: SessionMood[];
  relationship_log: RelationshipEvent[];
  user_tags: string[];
  provenance: {
    origin: 'user' | 'system';
    action: string;
  };
  last_modified_by: 'user' | 'system';
  editable_fields: string[];
  audit_log: AuditEvent[];
}

export interface AldenPersonaProps {
  personaId: string;
  traits: AldenTraits;
  motivationStyle: string;
  trustLevel: number;
  feedbackScore: number;
  habitConsistency: number;
  onTraitUpdate: (traits: Partial<AldenTraits>) => void;
  onExport: () => void;
  onCorrection: (correction: CorrectionEvent) => void;
  auditLog: AuditEvent[];
}

// Task Management (Alden Module)
export interface AldenTask {
  id: string;
  title: string;
  description?: string;
  status: 'todo' | 'in-progress' | 'completed' | 'archived';
  priority: 'low' | 'medium' | 'high' | 'critical';
  due_date?: string; // Changed to string (ISO 8601)
  created_at: string; // Changed to string (ISO 8601)
  updated_at: string; // Changed to string (ISO 8601)
  tags?: string[];
  subtasks?: AldenTask[];
  parent_id?: string;
  assignee?: string;
}

// For backward compatibility
export type Task = AldenTask;

export interface CreateAldenTaskRequest {
  userId?: string; // Optional - bridge will fallback to authenticated user
  title: string;
  description?: string;
  priority?: AldenTask['priority'];
  due_date?: string; // Changed to string (ISO 8601)
  tags?: string[];
  parent_id?: string;
  natural_language?: string; // For NLP parsing in Rust backend
}

// For backward compatibility with Alden module
export type CreateTaskRequest = CreateAldenTaskRequest;

export interface TaskUpdates {
  title?: string;
  description?: string;
  status?: Task['status'];
  priority?: Task['priority'];
  due_date?: string; // Changed to string (ISO 8601)
  tags?: string[];
}

export interface TaskViewPreferences {
  sortBy: 'priority' | 'due_date' | 'created_at' | 'title';
  filterBy?: 'status' | 'priority' | 'tags';
  groupBy?: 'status' | 'priority' | 'assignee';
}

export interface TaskAnalytics {
  total_tasks: number;
  completed_tasks: number;
  completion_rate: number;
  average_completion_time: number; // in hours
  tasks_by_priority: Record<Task['priority'], number>;
  tasks_by_status: Record<Task['status'], number>;
}

export interface TaskManagementProps {
  tasks: Task[];
  createTask: (task: CreateTaskRequest) => Promise<Task>;
  updateTask: (taskId: string, updates: TaskUpdates) => Promise<Task>;
  deleteTask: (taskId: string) => Promise<void>;
  viewPreferences: TaskViewPreferences;
  analytics: TaskAnalytics;
}

// High-priority directives (extend task with execution metadata)
export interface Directive extends Task {
  urgency_level: 'critical' | 'standard';
  protocol_ref: string; // e.g., Chimera
  issued_by?: string;
  acknowledged_at?: string;
}

