// ============================================================================
// Core - Session State Machine & Avatar Protocol
// Aligns with: docs/session_state_machine_design.md
// ============================================================================

/**
 * Session State Machine States
 * Controls lifecycle of multi-persona sessions
 */
export enum SessionState {
  IDLE = 'idle',
  INITIALIZING = 'initializing',
  ACTIVE = 'active',
  CONTEXT_SWITCHING = 'context_switching',
  BREAKOUT_ACTIVE = 'breakout_active',
  INTERRUPTED = 'interrupted',
  ARCHIVING = 'archiving',
  ARCHIVED = 'archived',
  ERROR = 'error'
}

/**
 * Persona Type Classification
 */
export enum PersonaType {
  CORE = 'core',           // Alden, Alice (system-defined)
  MIMIC = 'mimic',         // User-created Mimic personas
  EXTERNAL = 'external'    // Synapse-mediated external agents
}

/**
 * Session Context - Current state of session state machine
 */
export interface SessionContext {
  currentState: SessionState;
  activePersonaId: string | null;
  previousPersonaId: string | null;
  switchTimestamp: string | null;
  stateMetadata: Record<string, unknown>;
}

/**
 * Active Context - Currently active persona in a session
 */
export interface ActiveContext {
  personaId: string;           // Current active persona ('alden' | 'alice' | 'mimic-<id>')
  personaType: PersonaType;    // Type classification
  contextWindow: Message[];    // Last N messages visible to this persona
  memorySliceId: string;       // Reference to Vault memory slice
  permissions: string[];       // Active RBAC permissions
  streamState: AvatarStreamState; // Avatar stream metadata
}

/**
 * Context Switch Event - Logged when switching personas
 */
export interface ContextSwitchEvent {
  fromPersona: string;
  toPersona: string;
  timestamp: string;
  reason: 'user_initiated' | 'system_suggestion' | 'interrupt';
  preserveContext: boolean; // Whether to carry over conversation history
}

/**
 * Context Switch Options - Configuration for persona switching
 */
export interface ContextSwitchOptions {
  preserveHistory: boolean;    // Carry over last N messages
  historyLimit?: number;       // Default: 10
  notifyOtherPersonas: boolean; // Send context switch event
  auditSwitch: boolean;        // Log to audit trail (default: true)
}

/**
 * Context Memory Slice - Per-persona memory state
 */
export interface ContextMemorySlice {
  personaId: string;
  lastAccessedAt: string;
  messageHistory: Message[];
  customState: Record<string, unknown>;
}

/**
 * Message - Individual chat message in session
 */
export interface Message {
  id: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  personaId: string;
  timestamp: string;
  metadata?: {
    audioUrl?: string;
    visemeData?: VisemePayload[];
    attachments?: string[];
  };
}

/**
 * Participant - Session participant (persona or external agent)
 */
export interface Participant {
  type: 'persona' | 'external';
  id: string;
  displayName: string;
  avatarUrl?: string;
  joinedAt: string;
  status: 'active' | 'idle' | 'offline';
}

/**
 * Session Event - Logged event in session
 */
export interface SessionEvent {
  eventId: string;
  event: 'join' | 'leave' | 'response' | 'context_switch' | 'inter…1719 tokens truncated…ing;
  quota: number;
  usage: number;
  remaining: number;
  usage_percentage: number;
}

export interface MemoryUsageStats {
  conference_id: string;
  shared_memory: AllocationStats;
  individual_memory: AllocationStats;
  total_allocations: number;
  active_allocations: number;
  total_quota: number;
  total_usage: number;
  participant_stats: ParticipantMemoryStats[];
  calculated_at: string;
}

export interface AllocationResult {
  conference_id: string;
  allocations: MemoryAllocation[];
  shared_namespace_id: string;
  individual_namespace_ids: Record<string, string>;
  success: boolean;
  error?: string;
}

export interface DeallocationResult {
  conference_id: string;
  success: boolean;
  archived_count: number;
  deleted_namespaces_count: number;
  error?: string;
}

// ============================================================================
// Core - Workflow Orchestration
// ============================================================================

export type WorkflowPatternType =
  | 'sequential'
  | 'parallel'
  | 'loop'
  | 'conditional'
  | 'state_machine'
  | 'rules_driven'
  | 'collaborative';

export interface StateTransition {
  from_state: string;
  to_state: string;
  event: string;
  condition?: string;
  action?: string;
}

export interface ExecutionRule {
  rule_id: string;
  name: string;
  condition: string;
  action: string;
  priority: number;
}

export type CollaborationStrategy =
  | { type: 'voting'; quorum: number }
  | { type: 'parallel_merge'; merge_strategy: string }
  | { type: 'round_robin' }
  | { type: 'leader_follower'; leader: string }
  | { type: 'consensus'; timeout: number };

export type WorkflowPattern =
  | { type: 'sequential' }
  | { type: 'parallel' }
  | { type: 'loop'; max_iterations: number; exit_condition: string }
  | { type: 'conditional'; condition: string; on_true: WorkflowPattern; on_false: WorkflowPattern }
  | { type: 'state_machine'; initial_state: string; transitions: StateTransition[] }
  | { type: 'rules_driven'; rules: ExecutionRule[]; default_action: string }
  | { type: 'collaborative'; strategy: CollaborationStrategy };

export interface RetryConfig {
  max_attempts: number;
  delay_seconds: number;
  backoff_strategy: 'linear' | 'exponential' | 'fixed';
}

export interface WorkflowStep {
  step_id: string;
  name: string;
  description: string;
  assigned_agent: string;
  required_tools: string[];
  inputs: Record<string, JsonValue>;
  outputs: Record<string, JsonValue>;
  timeout?: number;
  retry?: RetryConfig;
}

export type WorkflowState =
  | 'pending'
  | 'running'
  | 'paused'
  | 'completed'
  | { failed: { error: string; failed_step: string } }
  | 'cancelled';

export interface Workflow {
  workflow_id: string;
  name: string;
  description: string;
  pattern: WorkflowPattern;
  steps: WorkflowStep[];
  conference_id?: string;
  state: WorkflowState;
  created_at: string; // Changed to string
  started_at?: string; // Changed to string
  completed_at?: string; // Changed to string
}

export type StepExecutionState =
  | 'pending'
  | 'running'
  | 'completed'
  | 'failed'
  | 'skipped';

export interface StepExecution {
  step_id: string;
  agent_id: string;
  state: StepExecutionState;
  inputs: Record<string, JsonValue>;
  outputs: Record<string, JsonValue>;
  started_at: string; // Changed to string
  completed_at?: string; // Changed to string
  error?: string;
  retry_count: number;
}

export interface WorkflowExecution {
  execution_id: string;
  workflow_id: string;
  conference_id: string;
  current_step?: string;
  state: WorkflowState;
  execution_history: StepExecution[];
  context: JsonObject;
  started_at: string; // Changed to string
  updated_at: string; // Changed to string
}

export interface CoreProps {
  currentSession: CoreSession;
  messages: CoreMessage[];
  routingRules: CoreRoutingRule[];
  conferences: Conference[];
  workflows: Workflow[];
  onMessageSend: (message: Omit<CoreMessage, 'message_id' | 'timestamp'>) => Promise<BridgeResponse<CoreMessage>>;
  onSessionEnd: () => Promise<BridgeResponse<void>>;
  onRuleUpdate: (rule: CoreRoutingRule) => Promise<BridgeResponse<CoreRoutingRule>>;
  onConferenceCreate: (conference: Omit<Conference, 'conference_id' | 'created_at'>) => Promise<Conference>;
  onWorkflowCreate: (workflow: Omit<Workflow, 'workflow_id' | 'created_at'>) => Promise<Workflow>;
}

// ============================================================================
// System Telemetry - Native runtime health signals for MythTech
// ============================================================================

export interface SystemTelemetry {
  cpuLoad: number;
  latencyMs: number;
  sampleIntervalMs: number;
  timestamp: string;
  source: string;
