    Archived,
}

/// Participant roles in a conference
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
#[serde(rename_all = "snake_case")]
pub enum ParticipantRole {
    /// Leads the conference, full permissions
    Leader,
    /// Moderates discussion, can manage participants
    Facilitator,
    /// Actively contributes to conference
    Contributor,
    /// Can view but not actively participate
    Observer,
}

/// Permissions for conference participants
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq, Hash)]
#[serde(rename_all = "snake_case")]
pub enum Permission {
    /// Can invite new participants
    CanInvite,
    /// Can pause/conclude conference
    CanModerate,
    /// Can read/write shared memory
    CanAccessSharedMemory,
    /// Can access own private memory
    CanAccessIndividualMemory,
    /// Can execute assigned tools
    CanUseTools,
    /// Can view all conference messages
    CanViewMessages,
    /// Can send messages in conference
    CanSendMessages,
    /// Can modify conference settings
    CanModifySettings,
}

