/// Main conference structure
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Conference {
    /// Unique conference identifier
    pub conference_id: String,
    /// Associated session ID
    pub session_id: String,
    /// Conference title
    pub title: String,
    /// Purpose or goal of the conference
    pub purpose: String,
    /// Type of conference
    pub conference_type: ConferenceType,
    /// List of participants
    pub participants: Vec<ConferenceParticipant>,
    /// ID of shared memory allocation
    pub shared_memory_id: Option<String>,
    /// ID of tool closet
    pub tool_closet_id: Option<String>,
    /// ID of active workflow (if any)
    pub workflow_id: Option<String>,
    /// Current state of the conference
    pub state: ConferenceState,
    /// When conference was created
    pub created_at: String,
    /// When conference was started (moved to Active state)
    pub started_at: Option<String>,
    /// When conference was concluded
    pub concluded_at: Option<String>,
    /// User who created the conference
    pub created_by: String,
}

#[allow(dead_code)]
impl Conference {
    /// Create a new conference
    pub fn new(
        session_id: String,
        title: String,
        purpose: String,
        conference_type: ConferenceType,
        created_by: String,
    ) -> Self {
        Self {
            conference_id: Uuid::new_v4().to_string(),
            session_id,
            title,
            purpose,
            conference_type,
            participants: Vec::new(),
            shared_memory_id: None,
            tool_closet_id: None,
            workflow_id: None,
            state: ConferenceState::Planning,
            created_at: chrono::Utc::now().to_rfc3339(),
            started_at: None,
            concluded_at: None,
            created_by,
        }
    }

    /// Add a participant to the conference
    pub fn add_participant(&mut self, participant: ConferenceParticipant) -> Result<(), String> {
        // Check if agent already in conference
        if self
            .participants
            .iter()
            .any(|p| p.agent_id == participant.agent_id)
        {
            return Err(format!(
                "Agent {} is already in conference",
                participant.agent_id
            ));
        }

        self.participants.push(participant);
        Ok(())
    }

    /// Remove a participant from the conference
    pub fn remove_participant(&mut self, agent_id: &str) -> Result<ConferenceParticipant, String> {
        let index = self
            .participants
            .iter()
            .position(|p| p.agent_id == agent_id)
            .ok_or_else(|| format!("Agent {} not found in conference", agent_id))?;

        Ok(self.participants.remove(index))
    }

    /// Get a participant by agent ID
    pub fn get_participant(&self, agent_id: &str) -> Option<&ConferenceParticipant> {
        self.participants.iter().find(|p| p.agent_id == agent_id)
    }

    /// Get a mutable participant by agent ID
    pub fn get_participant_mut(&mut self, agent_id: &str) -> Option<&mut ConferenceParticipant> {
        self.participants
            .iter_mut()
            .find(|p| p.agent_id == agent_id)
    }

    /// Start the conference (Planning ƒ+' Active)
    pub fn start(&mut self) -> Result<(), String> {
        if self.state != ConferenceState::Planning {
            return Err(format!("Cannot start conference in state {:?}", self.state));
        }

        if self.participants.is_empty() {
            return Err("Cannot start conference with no participants".to_string());
        }

        self.state = ConferenceState::Active;
        self.started_at = Some(chrono::Utc::now().to_rfc3339());
        Ok(())
    }

    /// Pause the conference (Active ƒ+' Paused)
    pub fn pause(&mut self) -> Result<(), String> {
        if self.state != ConferenceState::Active {
            return Err(format!("Cannot pause conference in state {:?}", self.state));
        }

        self.state = ConferenceState::Paused;
        Ok(())
    }

    /// Resume the conference (Paused ƒ+' Active)
    pub fn resume(&mut self) -> Result<(), String> {
        if self.state != ConferenceState::Paused {
            return Err(format!(
                "Cannot resume conference in state {:?}",
                self.state
            ));
        }

        self.state = ConferenceState::Active;
        Ok(())
    }

    /// Conclude the conference (Active/Paused ƒ+' Concluded)
    pub fn conclude(&mut self) -> Result<(), String> {
        if self.state != ConferenceState::Active && self.state != ConferenceState::Paused {
            return Err(format!(
                "Cannot conclude conference in state {:?}",
                self.state
            ));
        }

        self.state = ConferenceState::Concluded;
        self.concluded_at = Some(chrono::Utc::now().to_rfc3339());
        Ok(())
    }

    /// Archive the conference (Concluded ƒ+' Archived)
    pub fn archive(&mut self) -> Result<(), String> {
        if self.state != ConferenceState::Concluded {
            return Err(format!(
                "Cannot archive conference in state {:?}",
                self.state
            ));
        }

        self.state = ConferenceState::Archived;
        Ok(())
    }

    /// Get default memory quota based on conference type
    pub fn default_shared_memory_quota(&self) -> usize {
        match self.conference_type {
            ConferenceType::Brainstorm => 5000,
            ConferenceType::ProblemSolving => 3000,
            ConferenceType::Review => 1000,
            ConferenceType::Planning => 4000,
            ConferenceType::Research => 10000,
        }
