#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_conference_creation() {
        let conference = Conference::new(
            "session_123".to_string(),
            "Test Conference".to_string(),
            "Testing conference creation".to_string(),
            ConferenceType::Brainstorm,
            "user_456".to_string(),
        );

        assert_eq!(conference.state, ConferenceState::Planning);
        assert_eq!(conference.participants.len(), 0);
        assert!(conference.started_at.is_none());
    }

    #[test]
    fn test_add_participant() {
        let mut conference = Conference::new(
            "session_123".to_string(),
            "Test Conference".to_string(),
            "Testing participants".to_string(),
            ConferenceType::Brainstorm,
            "user_456".to_string(),
        );

        let participant = ConferenceParticipant::new("alden".to_string(), ParticipantRole::Leader);
        conference.add_participant(participant).unwrap();

        assert_eq!(conference.participants.len(), 1);
        assert_eq!(conference.participants[0].agent_id, "alden");
    }

    #[test]
    fn test_participant_permissions() {
        let leader = ConferenceParticipant::new("alden".to_string(), ParticipantRole::Leader);
        assert!(leader.has_permission(&Permission::CanModerate));
        assert!(leader.has_permission(&Permission::CanInvite));

        let observer = ConferenceParticipant::new("alice".to_string(), ParticipantRole::Observer);
        assert!(!observer.has_permission(&Permission::CanModerate));
        assert!(observer.has_permission(&Permission::CanViewMessages));
    }

    #[test]
    fn test_conference_lifecycle() {
        let mut conference = Conference::new(
            "session_123".to_string(),
            "Test Conference".to_string(),
            "Testing lifecycle".to_string(),
            ConferenceType::Brainstorm,
            "user_456".to_string(),
        );

        // Cannot start without participants
        assert!(conference.start().is_err());

        // Add participant
        let participant = ConferenceParticipant::new("alden".to_string(), ParticipantRole::Leader);
        conference.add_participant(participant).unwrap();

        // Start conference
        assert!(conference.start().is_ok());
        assert_eq!(conference.state, ConferenceState::Active);
        assert!(conference.started_at.is_some());

        // Pause conference
        assert!(conference.pause().is_ok());
        assert_eq!(conference.state, ConferenceState::Paused);

        // Resume conference
        assert!(conference.resume().is_ok());
        assert_eq!(conference.state, ConferenceState::Active);

        // Conclude conference
        assert!(conference.conclude().is_ok());
        assert_eq!(conference.state, ConferenceState::Concluded);
        assert!(conference.concluded_at.is_some());

        // Archive conference
        assert!(conference.archive().is_ok());
        assert_eq!(conference.state, ConferenceState::Archived);
    }

