    /// Get memory usage statistics for a conference
    pub async fn get_memory_usage_stats(
        &self,
        conference_id: &str,
    ) -> Result<memory_allocation::MemoryUsageStats, String> {
        let allocations = self.get_memory_allocations(conference_id).await?;

        let mut shared_quota = 0;
        let mut shared_usage = 0;
        let mut individual_total_quota = 0;
        let mut individual_total_usage = 0;
        let mut participant_stats = Vec::new();

        for allocation in &allocations {
            match &allocation.namespace_type {
                memory_allocation::NamespaceType::Shared => {
                    shared_quota = allocation.quota;
                    shared_usage = allocation.current_usage;
                }
                memory_allocation::NamespaceType::Individual { agent_id } => {
                    individual_total_quota += allocation.quota;
                    individual_total_usage += allocation.current_usage;

                    participant_stats.push(memory_allocation::ParticipantMemoryStats {
                        agent_id: agent_id.clone(),
                        namespace_id: allocation.vault_namespace_id.clone(),
                        quota: allocation.quota,
                        usage: allocation.current_usage,
                        remaining: allocation.remaining_quota(),
                        usage_percentage: allocation.usage_percentage(),
                    });
                }
            }
        }

        let shared_memory = memory_allocation::AllocationStats {
            total_quota: shared_quota,
            total_usage: shared_usage,
            remaining: if shared_usage >= shared_quota {
                0
            } else {
                shared_quota - shared_usage
            },
            usage_percentage: if shared_quota == 0 {
                0.0
            } else {
                (shared_usage as f32 / shared_quota as f32) * 100.0
            },
            over_quota: shared_usage >= shared_quota,
        };

        let individual_memory = memory_allocation::AllocationStats {
            total_quota: individual_total_quota,
            total_usage: individual_total_usage,
            remaining: if individual_total_usage >= individual_total_quota {
                0
            } else {
                individual_total_quota - individual_total_usage
            },
            usage_percentage: if individual_total_quota == 0 {
                0.0
            } else {
                (individual_total_usage as f32 / individual_total_quota as f32) * 100.0
            },
            over_quota: individual_total_usage >= individual_total_quota,
        };

        Ok(memory_allocation::MemoryUsageStats {
            conference_id: conference_id.to_string(),
            total_allocations: allocations.len(),
            active_allocations: allocations
                .iter()
                .filter(|a| a.status == memory_allocation::AllocationStatus::Active)
                .count(),
            total_quota: shared_quota + individual_total_quota,
            total_usage: shared_usage + individual_total_usage,
            shared_memory,
            individual_memory,
            participant_stats,
            calculated_at: chrono::Utc::now().to_rfc3339(),
        })
    }

    /// Deallocate memory for a conference (archives and optionally deletes)
    pub async fn deallocate_memory(
        &self,
        conference_id: &str,
        _options: memory_allocation::ArchiveOptions,
    ) -> Result<DeallocationResult, String> {
        let allocations = self.get_memory_allocations(conference_id).await?;

        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().pool;

        let mut archived_count = 0;
        let deleted_namespaces_count = allocations.len();

        // Archive allocations
        for allocation in allocations {
            let now = chrono::Utc::now().to_rfc3339();
            let status_json =
                serde_json::to_string(&memory_allocation::AllocationStatus::Archived).unwrap();

            sqlx::query(
                "UPDATE core_memory_allocations SET status = ?, updated_at = ?, archived_at = ? WHERE allocation_id = ?"
            )
            .bind(&status_json)
            .bind(&now)
            .bind(&now)
            .bind(&allocation.allocation_id)
            .execute(pool)
            .await
            .map_err(|e| format!("Failed to archive allocation: {}", e))?;

            archived_count += 1;

            // TODO: If preserve_data is true, copy Vault namespace data to permanent storage
            // TODO: If preserve_data is false, delete Vault namespace
        }

        Ok(DeallocationResult {
            conference_id: conference_id.to_string(),
            archived_count,
            deleted_namespaces_count,
            success: true,
            error: None,
        })
    }

