    /// Initialize tool closet schema
    pub async fn initialize_tool_schema(&self) -> Result<(), String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().pool;

        // Tools registry table
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS core_tools (
                tool_id TEXT PRIMARY KEY,
                name TEXT NOT NULL,
                description TEXT,
                tool_type TEXT NOT NULL,
                required_capability TEXT NOT NULL,
                category TEXT NOT NULL,
                enabled INTEGER DEFAULT 1,
                tags TEXT NOT NULL,
                created_at TEXT NOT NULL
            )
            "#,
        )
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to create tools table: {}", e))?;

        // Tool assignments table
        sqlx::query(
            r#"
            CREATE TABLE IF NOT EXISTS core_tool_assignments (
                assignment_id TEXT PRIMARY KEY,
                conference_id TEXT NOT NULL,
                agent_id TEXT NOT NULL,
                tool_id TEXT NOT NULL,
                assigned_at TEXT NOT NULL,
                usage_count INTEGER DEFAULT 0,
                last_used TEXT,
                FOREIGN KEY (conference_id) REFERENCES core_conferences (conference_id) ON DELETE CASCADE,
                FOREIGN KEY (tool_id) REFERENCES core_tools (tool_id),
                UNIQUE(conference_id, agent_id, tool_id)
            )
            "#,
        )
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to create tool_assignments table: {}", e))?;

        Ok(())
    }

    /// Assign tools to a conference participant
    pub async fn assign_tools(
        &self,
        conference_id: &str,
        agent_id: &str,
        tool_ids: Vec<String>,
    ) -> Result<ToolAssignmentResult, String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().pool;

        let mut assigned_tools = Vec::new();

        for tool_id in tool_ids {
            // Check if tool exists
            let tool_exists: Option<(i64,)> =
                sqlx::query_as("SELECT COUNT(*) FROM core_tools WHERE tool_id = ?")
                    .bind(&tool_id)
                    .fetch_optional(pool)
                    .await
                    .map_err(|e| format!("Failed to check tool: {}", e))?;

            if tool_exists.is_none() || tool_exists.unwrap().0 == 0 {
                continue; // Skip non-existent tools
            }

            let assignment = ToolAssignment::new(
                conference_id.to_string(),
                agent_id.to_string(),
                tool_id.clone(),
            );

            // Insert assignment (or update if exists)
            sqlx::query(
                r#"
                INSERT INTO core_tool_assignments
                (assignment_id, conference_id, agent_id, tool_id, assigned_at, usage_count, last_used)
                VALUES (?, ?, ?, ?, ?, ?, ?)
                ON CONFLICT(conference_id, agent_id, tool_id) DO NOTHING
                "#,
            )
            .bind(&assignment.assignment_id)
            .bind(&assignment.conference_id)
            .bind(&assignment.agent_id)
            .bind(&assignment.tool_id)
            .bind(&assignment.assigned_at)
            .bind(assignment.usage_count)
            .bind(&assignment.last_used)
            .execute(pool)
            .await
            .map_err(|e| format!("Failed to assign tool: {}", e))?;

            assigned_tools.push(assignment);
        }

        Ok(ToolAssignmentResult {
            conference_id: conference_id.to_string(),
            agent_id: agent_id.to_string(),
            assigned_tools,
            success: true,
            error: None,
        })
    }

