    /// List all available tools (optionally filtered by category)
    pub async fn list_tools(&self, category: Option<ToolCategory>) -> Result<Vec<Tool>, String> {
        let db = self.db.read().await;
        let pool = &db.as_ref().unwrap().pool;

        #[derive(sqlx::FromRow)]
        struct ToolRow {
            tool_id: String,
            name: String,
            description: Option<String>,
            tool_type: String,
            required_capability: String,
            category: String,
            enabled: i32,
            tags: String,
            created_at: String,
        }

        let query = if let Some(cat) = category {
            let cat_json = serde_json::to_string(&cat).unwrap();
            sqlx::query_as("SELECT * FROM core_tools WHERE category = ? AND enabled = 1")
                .bind(cat_json)
        } else {
            sqlx::query_as("SELECT * FROM core_tools WHERE enabled = 1")
        };

        let rows: Vec<ToolRow> = query
            .fetch_all(pool)
            .await
            .map_err(|e| format!("Failed to list tools: {}", e))?;

        Ok(rows
            .into_iter()
            .map(|row| Tool {
                tool_id: row.tool_id,
                name: row.name,
                description: row.description.unwrap_or_default(),
                tool_type: serde_json::from_str(&row.tool_type).unwrap(),
                required_capability: serde_json::from_str(&row.required_capability).unwrap(),
                category: serde_json::from_str(&row.category).unwrap(),
                enabled: row.enabled != 0,
                tags: serde_json::from_str(&row.tags).unwrap(),
