            let vault = app.state::<secure_vault::SecureVault>();
            if let Err(e) = tauri::async_runtime::block_on(vault.initialize(&app_handle)) {
                eprintln!("Failed to initialize secure vault: {}", e);
                let _ = tauri::api::dialog::message(
                    Some(&window),
                    "Vault Initialization Error",
                    &format!("Failed to initialize secure vault: {}", e),
                );
            } else {
                println!("Secure vault initialized successfully");
            }

            // DISABLED: Pure Tauri IPC mode - no HTTP services needed
            // ServiceManager startup commented out for pure IPC architecture
            // Alden, Synapse, and Vault now use direct Tauri IPC commands

            println!("Hearthlink running in pure Tauri IPC mode (no HTTP services)");

            // Set up cleanup on app shutdown
            window.on_window_event({
                move |event| {
                    if let tauri::WindowEvent::CloseRequested { .. } = event {
                        println!("Application closing, stopping all services...");
                    }
                }
            });

            window.set_focus().unwrap();
            window.show().unwrap();

            Ok(())
        })
        .invoke_handler(invoke_handler)
