/// Remove participant from conference
#[tauri::command]
pub async fn core_participant_remove(
    conference_id: String,
    agent_id: String,
    state: State<'_, ConferenceManager>,
) -> Result<Conference, String> {
    let mut conference = state.get_conference(&conference_id).await?;
    conference.remove_participant(&agent_id)?;

    // Update database
    let db = state.db.read().await;
    let pool = &db.as_ref().unwrap().pool;

    // Update participants JSON
    let participants_json = serde_json::to_string(&conference.participants).unwrap();
    sqlx::query("UPDATE core_conferences SET participants = ? WHERE conference_id = ?")
        .bind(&participants_json)
        .bind(&conference_id)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to update conference: {}", e))?;

    // Delete from participants table
    sqlx::query(
        "DELETE FROM core_conference_participants WHERE conference_id = ? AND agent_id = ?",
    )
    .bind(&conference_id)
    .bind(&agent_id)
    .execute(pool)
    .await
    .map_err(|e| format!("Failed to delete participant: {}", e))?;

    Ok(conference)
}

/// Update participant role
#[tauri::command]
pub async fn core_participant_update_role(
    request: UpdateParticipantRoleRequest,
    state: State<'_, ConferenceManager>,
) -> Result<Conference, String> {
    let mut conference = state.get_conference(&request.conference_id).await?;

    // Update role and recalculate permissions
    if let Some(participant) = conference.get_participant_mut(&request.agent_id) {
        participant.role = request.role.clone();
        participant.permissions = super::conference::ConferenceParticipant::new(
            request.agent_id.clone(),
            request.role.clone(),
        )
        .permissions;
    } else {
        return Err(format!("Participant {} not found", request.agent_id));
    }

    // Update database
    let db = state.db.read().await;
    let pool = &db.as_ref().unwrap().pool;

    let participant = conference.get_participant(&request.agent_id).unwrap();
    let participants_json = serde_json::to_string(&conference.participants).unwrap();
    let permissions_json = serde_json::to_string(&participant.permissions).unwrap();

    sqlx::query("UPDATE core_conferences SET participants = ? WHERE conference_id = ?")
        .bind(&participants_json)
        .bind(&request.conference_id)
        .execute(pool)
        .await
        .map_err(|e| format!("Failed to update conference: {}", e))?;

    sqlx::query(
        "UPDATE core_conference_participants SET role = ?, permissions = ? WHERE conference_id = ? AND agent_id = ?"
    )
    .bind(serde_json::to_string(&request.role).unwrap())
    .bind(&permissions_json)
    .bind(&request.conference_id)
    .bind(&request.agent_id)
    .execute(pool)
    .await
    .map_err(|e| format!("Failed to update participant: {}", e))?;

    Ok(conference)
}

