/**
 * Alice Agent Bridge Implementation - Tauri IPC Version
 *
 * Provides React/TypeScript bridge for Alice agent system using pure Tauri IPC.
 * This version uses invoke() calls instead of HTTP requests.
 *
 * Features:
 * - Behavioral profile analysis and management
 * - Insight generation and tracking
 * - Pattern recognition (communication, attention, emotional, learning)
 * - Context-aware recommendations
 *
 * @version 2.0.0
 * @date 2025-10-26
 */

import { invoke } from '@tauri-apps/api/tauri';
import { BaseBridge, BridgeConfig } from './BaseBridge';
import {
  AliceProps,
  AliceBehavioralProfile,
  AliceInsight,
  BridgeResponse,
  CommandRequest,
  JsonObject,
} from '../types/agents';

type DeepWorkSession = {
  session_id: string;
  user_id: string;
  start_time: string;
  end_time?: string | null;
  duration_seconds?: number | null;
  goal?: string | null;
  status: 'active' | 'completed' | 'aborted';
  created_at: string;
};

type DistractionBlockState = {
  enabled: boolean;
  blocked_items: string[];
  applied_at: string;
  note: string;
};

type DeepWorkSessionResult = {
  session: DeepWorkSession;
  distraction_blocking: DistractionBlockState;
};

type DeepWorkStatus = {
  active: boolean;
  current_session?: DeepWorkSession | null;
  last_session?: DeepWorkSession | null;
  distraction_blocking: DistractionBlockState;
};

export class AliceBridge extends BaseBridge<AliceProps> {
  private profile: AliceBehavioralProfile | null = null;
  private insights: AliceInsight[] = [];

  constructor(config: Partial<BridgeConfig> & { userId: string }) {
    super({
      agentId: 'alice',
      ...config,
    });
    // Initialize with default profile immediately to prevent null errors
    this.profile = {
      user_id: this.userId || 'default_user',
      communication_style: 'analytical',
      preferred_interaction_times: ['09:00-12:00', '14:00-17:00'],
      attention_patterns: {
        focus_duration_avg: 45.0,
        distraction_triggers: ['notifications', 'background noise'],
        optimal_work_periods: ['morning', 'late afternoon'],
      },
      emotional_patterns: {
        stress_indicators: ['increased typing speed', 'shorter responses'],
        motivation_factors: ['achievement', 'recognition'],
        mood_trends: [],
      },
      learning_preferences: {
        preferred_formats: ['visual', 'interactive'],
        comprehension_pace: 'moderate',
        feedback_style: 'constructive',
      },
    };
  }

  /**
   * Initialize Alice bridge and load behavioral profile
   */
  protected async onInitialize(): Promise<void> {
    try {
      // Initialize with default profile
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: ['09:00-12:00', '14:00-17:00'],
        attention_patterns: {
          focus_duration_avg: 45.0,
          distraction_triggers: ['notifications', 'background noise'],
          optimal_work_periods: ['morning', 'late afternoon'],
        },
        emotional_patterns: {
          stress_indicators: ['increased typing speed', 'shorter responses'],
          motivation_factors: ['achievement', 'recognition'],
          mood_trends: [
            { date: new Date('2024-01-01').toISOString(), mood: 'positive', score: 8.5 },
            { date: new Date('2024-01-02').toISOString(), mood: 'positive', score: 7.8 },
          ],
        },
        learning_preferences: {
          preferred_formats: ['visual', 'interactive'],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };

      // Initialize with empty insights
      this.insights = [];
    } catch (error) {
      console.error('[AliceBridge] Failed to initialize:', error);
      // Don't throw - allow degraded mode
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: [],
        attention_patterns: {
          focus_duration_avg: 0,
          distraction_triggers: [],
          optimal_work_periods: [],
        },
        emotional_patterns: {
          stress_indicators: [],
          motivation_factors: [],
          mood_trends: [],
        },
        learning_preferences: {
          preferred_formats: [],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };
    }
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.profile = null;
    this.insights = [];
  }

  /**
   * Get props for Alice React component
   */
  async getProps(): Promise<AliceProps> {
    if (!this.profile) {
      console.warn('[AliceBridge.getProps] WARNING - Behavioral profile not loaded, using fallback');
      // Fallback to default profile to prevent UI crash
      this.profile = {
        user_id: this.userId || 'default_user',
        communication_style: 'analytical',
        preferred_interaction_times: [],
        attention_patterns: {
          focus_duration_avg: 45.0,
          distraction_triggers: [],
          optimal_work_periods: [],
        },
        emotional_patterns: {
          stress_indicators: [],
          motivation_factors: [],
          mood_trends: [],
        },
        learning_preferences: {
          preferred_formats: [],
          comprehension_pace: 'moderate',
          feedback_style: 'constructive',
        },
      };
    }

    const props = {
      profile: this.profile,
      insights: this.insights,
      onProfileUpdate: this.updateProfile.bind(this),
      onInsightDismiss: this.dismissInsight.bind(this),
    };

    console.log(`[AliceBridge:${this.agentId}] getProps returning props`);
    return props;
  }

  // ============================================================================
  // Profile Management
  // ============================================================================

  /**
   * Update behavioral profile
   */
  async updateProfile(
    updates: Partial<AliceBehavioralProfile>
  ): Promise<BridgeResponse<AliceBehavioralProfile>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        communication_style: updates.communication_style,
        attention_patterns: updates.attention_patterns,
        emotional_patterns: updates.emotional_patterns,
        learning_preferences: updates.learning_preferences,
        // feedback_insights: updates.feedback_insights, // Removed this line
      };

      const updated = await invoke<AliceBehavioralProfile>('alice_update_profile', { request });

      if (this.profile) {
        this.profile = { ...this.profile, ...updated };
      }

      this.notifySubscribers({ type: 'profile_updated', data: updated });

      if (!this.profile) {
        return { success: true, data: updated };
      }
      return { success: true, data: this.profile };
    } catch (error) {
      return this.createErrorResponse<AliceBehavioralProfile>(
        error,
        'Failed to update profile',
        'PROFILE_UPDATE_ERROR'
      );
    }
  }

  /**
   * Get behavioral profile
   */
  async getProfile(): Promise<BridgeResponse<AliceBehavioralProfile>> {
    try {
      const profile = await invoke<AliceBehavioralProfile>('alice_get_profile', {
        userId: this.userId, // Use this.userId
      });

      this.profile = profile;
      return { success: true, data: this.profile };
    } catch (error) {
      return this.createErrorResponse<AliceBehavioralProfile>(
        error,
        'Failed to fetch profile',
        'PROFILE_FETCH_ERROR'
      );
    }
  }

  // ============================================================================
  // Insight Management
  // ============================================================================

  /**
   * Get all insights
   */
  async getInsights(
    category?: AliceInsight['category']
  ): Promise<BridgeResponse<AliceInsight[]>> {
    try {
      const insights = await invoke<AliceInsight[]>('alice_get_insights', {
        userId: this.userId, // Use this.userId
        category,
      });

      this.insights = insights;
      return { success: true, data: this.insights };
    } catch (error) {
      return this.createErrorResponse<AliceInsight[]>(
        error,
        'Failed to fetch insights',
        'INSIGHT_FETCH_ERROR'
      );
    }
  }

  /**
   * Dismiss an insight
   */
  async dismissInsight(insightId: string): Promise<BridgeResponse<void>> {
    try {
      await invoke('alice_dismiss_insight', { insightId });

      this.insights = this.insights.filter(i => i.insight_id !== insightId);
      this.notifySubscribers({ type: 'insight_dismissed', data: { insightId } });

      return { success: true, data: undefined };
    } catch (error) {
      return this.createErrorResponse<void>(
        error,
        'Failed to dismiss insight',
        'INSIGHT_DISMISS_ERROR'
      );
    }
  }

  /**
   * Request new insight generation
   */
  async generateInsights(context?: JsonObject): Promise<BridgeResponse<AliceInsight[]>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        category: 'behavioral',
        title: 'Generated Insight',
        content: JSON.stringify(context || {}),
        confidence_score: 0.8,
      };

      const newInsights = await invoke<AliceInsight[]>('alice_create_insight', { request });

      this.insights.push(...newInsights);
      this.notifySubscribers({ type: 'insights_generated', data: newInsights });

      return { success: true, data: newInsights };
    } catch (error) {
      return this.createErrorResponse<AliceInsight[]>(
        error,
        'Failed to generate insights',
        'INSIGHT_GENERATE_ERROR'
      );
    }
  }

  // ============================================================================
  // Pattern Analysis
  // ============================================================================

  /**
   * Analyze communication patterns
   */
  async analyzeCommunicationPatterns(): Promise<BridgeResponse<{
    style: string;
    preferred_times: string[];
    suggestions: string[];
  }>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId, // Use this.userId
        analysis_type: 'communication',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<{
        style: string;
        preferred_times: string[];
        suggestions: string[];
      }>('alice_analyze_patterns', { request });

      this.notifySubscribers({ type: 'communication_analyzed', data: analysis });

      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to analyze communication patterns',
        'COMMUNICATION_ANALYSIS_ERROR'
      );
    }
  }

