      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to stop Deep Work session',
        'DEEP_WORK_STOP_ERROR'
      );
    }
  }

  /**
   * Get Deep Work session history
   */
  async getDeepWorkHistory(limit: number = 20): Promise<BridgeResponse<DeepWorkSession[]>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        userId: this.userId,
        limit,
      };

      const history = await invoke<DeepWorkSession[]>('alice_get_deep_work_history', { request });
      return { success: true, data: history };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to get Deep Work history',
        'DEEP_WORK_HISTORY_ERROR'
      );
    }
  }
  /**
   * Get full Deep Work analysis
   */
  async getDeepWorkAnalysis(): Promise<BridgeResponse<any>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        analysis_type: 'general',
        time_range: 'last_30_days',
      };

      const analysis = await invoke<any>('alice_analyze_patterns', { request });
      this.notifySubscribers({ type: 'deep_work_analyzed', data: analysis });
      return { success: true, data: analysis };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to get Deep Work analysis',
        'DEEP_WORK_ANALYSIS_ERROR'
      );
    }
  }

  /**
   * Get Deep Work status (active session + distraction blocking state)
   */
  async getDeepWorkStatus(): Promise<BridgeResponse<DeepWorkStatus>> {
    try {
      const request: CommandRequest = {
        user_id: this.userId,
        userId: this.userId,
      };

      const status = await invoke<DeepWorkStatus>('alice_get_deep_work_status', { request });
      return { success: true, data: status };
    } catch (error) {
      return this.createErrorResponse(
        error,
        'Failed to get Deep Work status',
        'DEEP_WORK_STATUS_ERROR'
      );
    }
  }
}

